00189     private async Task HandleOnMouseOutAsync(MouseEventArgs mouseEventArgs)
00190     {
00191         if (IsBeingDragged)
00192             return;
00193 
00194         if (_thinksLeftMouseButtonIsDown && Tab is IDrag draggable)
00195         {
00196             var measuredHtmlElementDimensions = await JsRuntime.GetLuthetusCommonApi()
00197                 .MeasureElementById(HtmlId)
00198                 .ConfigureAwait(false);
00199 
00200             await draggable.OnDragStartAsync().ConfigureAwait(false);
00201 
00202             // Width
00203             {
00204                 var widthDimensionAttribute = draggable.DragElementDimensions.DimensionAttributeList.First(
00205                     x => x.DimensionAttributeKind == DimensionAttributeKind.Width);
00206     
00207                 widthDimensionAttribute.DimensionUnitList.Clear();
00208                 widthDimensionAttribute.DimensionUnitList.Add(new DimensionUnit
00209                 {
00210                     Value = measuredHtmlElementDimensions.WidthInPixels,
00211                     DimensionUnitKind = DimensionUnitKind.Pixels
00212                 });
00213             }
00214 
00215             // Height
00216             {
00217                 var heightDimensionAttribute = draggable.DragElementDimensions.DimensionAttributeList.First(
00218                     x => x.DimensionAttributeKind == DimensionAttributeKind.Height);
00219     
00220                 heightDimensionAttribute.DimensionUnitList.Clear();
00221                 heightDimensionAttribute.DimensionUnitList.Add(new DimensionUnit
00222                 {
00223                     Value = measuredHtmlElementDimensions.HeightInPixels,
00224                     DimensionUnitKind = DimensionUnitKind.Pixels
00225                 });
00226             }
00227 
00228             // Left
00229             {
00230                 var leftDimensionAttribute = draggable.DragElementDimensions.DimensionAttributeList.First(
00231                     x => x.DimensionAttributeKind == DimensionAttributeKind.Left);
00232     
00233                 leftDimensionAttribute.DimensionUnitList.Clear();
00234                 leftDimensionAttribute.DimensionUnitList.Add(new DimensionUnit
00235                 {
00236                     Value = mouseEventArgs.ClientX,
00237                     DimensionUnitKind = DimensionUnitKind.Pixels
00238                 });
00239             }
00240 
00241             // Top
00242             {
00243                 var topDimensionAttribute = draggable.DragElementDimensions.DimensionAttributeList.First(
00244                     x => x.DimensionAttributeKind == DimensionAttributeKind.Top);
00245     
00246                 topDimensionAttribute.DimensionUnitList.Clear();
00247                 topDimensionAttribute.DimensionUnitList.Add(new DimensionUnit
00248                 {
00249                     Value = mouseEventArgs.ClientY,
00250                     DimensionUnitKind = DimensionUnitKind.Pixels
00251                 });
00252             }
00253 
00254             draggable.DragElementDimensions.ElementPositionKind = ElementPositionKind.Fixed;
00255 
00256             SubscribeToDragEventForScrolling(draggable);
00257         }
00258     }
