00260     public void SubscribeToDragEventForScrolling(IDrag draggable)
00261     {
00262         if (IsBeingDragged)
00263             return;
00264 
00265         _dragEventHandler = DragEventHandlerAsync;
00266 
00267         Dispatcher.Dispatch(new DragState.ShouldDisplayAndMouseEventArgsAndDragSetAction(true, null, draggable));
00268     }
00269 
00270     private Task DragEventHandlerAsync(
00271         (MouseEventArgs firstMouseEventArgs, MouseEventArgs secondMouseEventArgs) mouseEventArgsTuple)
00272     {
00273         if (IsBeingDragged)
00274             return Task.CompletedTask;
00275 
00276         var localThinksLeftMouseButtonIsDown = _thinksLeftMouseButtonIsDown;
00277 
00278         if (!localThinksLeftMouseButtonIsDown)
00279             return Task.CompletedTask;
00280 
00281         // Buttons is a bit flag '& 1' gets if left mouse button is held
00282         if (Tab is IDrag draggable &&
00283             localThinksLeftMouseButtonIsDown &&
00284             (mouseEventArgsTuple.secondMouseEventArgs.Buttons & 1) == 1)
00285         {
00286             ResizeHelper.Move(
00287                 draggable.DragElementDimensions,
00288                 mouseEventArgsTuple.firstMouseEventArgs,
00289                 mouseEventArgsTuple.secondMouseEventArgs);
00290         }
00291         else
00292         {
00293             _dragEventHandler = null;
00294             _previousDragMouseEventArgs = null;
00295             _thinksLeftMouseButtonIsDown = false;
00296         }
00297 
00298         return Task.CompletedTask;
00299     }
