00060     public void EnqueueCommand(TerminalCommandRequest terminalCommandRequest)
00061     {
00062         _backgroundTaskService.Enqueue(
00063             Key<IBackgroundTask>.NewKey(),
00064             BlockingBackgroundTaskWorker.GetQueueKey(),
00065             "Enqueue Command",
00066             () => HandleCommand(terminalCommandRequest));
00067     }
