00083     public void ClearFireAndForget()
00084     {
00085         var localHasExecutingProcess = HasExecutingProcess;
00086     
00087         _ = Task.Run(() =>
00088         {
00089             if (localHasExecutingProcess)
00090             {
00091                 TerminalOutput.ClearOutputExceptMostRecentCommand();
00092             }
00093             else
00094             {
00095                 TerminalOutput.ClearOutput();
00096             }
00097             
00098             return Task.CompletedTask;
00099         });
00100     }
