00102     private async Task HandleCommand(TerminalCommandRequest terminalCommandRequest)
00103     {
00104         TerminalOutput.ClearHistoryWhenExistingOutputTooLong();
00105     
00106         var parsedCommand = await TerminalInteractive.TryHandleCommand(terminalCommandRequest);
00107         ActiveTerminalCommandParsed = parsedCommand;
00108 
00109         if (parsedCommand is null)
00110             return;
00111         
00112         var cliWrapCommand = Cli.Wrap(parsedCommand.TargetFileName);
00113 
00114         cliWrapCommand = cliWrapCommand.WithWorkingDirectory(TerminalInteractive.WorkingDirectory);
00115 
00116         if (!string.IsNullOrWhiteSpace(parsedCommand.Arguments))
00117             cliWrapCommand = cliWrapCommand.WithArguments(parsedCommand.Arguments);
00118         
00119         // TODO: Decide where to put invocation of 'parsedCommand.SourceTerminalCommandRequest.BeginWithFunc'...
00120         //       ...and invocation of 'cliWrapCommand'
00121         //       and invocation of 'parsedCommand.SourceTerminalCommandRequest.ContinueWithFunc'
00122         //       |
00123         //    This comment is referring to the 'try/catch' block logic.
00124         //       If the 'BeginWithFunc' throws an exception should the 'cliWrapCommand' run?
00125         //       If the 'cliWrapCommand' throws an exception should the 'ContinueWithFunc' run?
00126         
00127         try
00128         {
00129             HasExecutingProcess = true;
00130         
00131             if (parsedCommand.SourceTerminalCommandRequest.BeginWithFunc is not null)
00132             {
00133                 await parsedCommand.SourceTerminalCommandRequest.BeginWithFunc
00134                     .Invoke(parsedCommand)
00135                     .ConfigureAwait(false);
00136             }
00137             
00138             await cliWrapCommand
00139                 .Observe(_commandCancellationTokenSource.Token)
00140                 .ForEachAsync(HandleOutput)
00141                 .ConfigureAwait(false);
00142         }
00143         catch (Exception e)
00144         {
00145             // TODO: This will erroneously write 'StartedCommandEvent' out twice...
00146             //       ...unless a check is added to see WHEN the exception was thrown.
00147             TerminalOutput.WriteOutput(
00148                 parsedCommand,
00149                 new StartedCommandEvent(-1));
00150         
00151             TerminalOutput.WriteOutput(
00152                 parsedCommand,
00153                 new StandardErrorCommandEvent(
00154                     parsedCommand.SourceTerminalCommandRequest.CommandText +
00155                     " threw an exception" +
00156                     "\n"));
00157         
00158             NotificationHelper.DispatchError("Terminal Exception", e.ToString(), _commonComponentRenderers, _dispatcher, TimeSpan.FromSeconds(14));
00159         }
00160         finally
00161         {
00162             if (parsedCommand.SourceTerminalCommandRequest.ContinueWithFunc is not null)
00163             {
00164                 await parsedCommand.SourceTerminalCommandRequest.ContinueWithFunc
00165                     .Invoke(parsedCommand)
00166                     .ConfigureAwait(false);
00167             }
00168         
00169             HasExecutingProcess = false;
00170         }
00171     }
00172     
00173     private void HandleOutput(CommandEvent commandEvent)
