00185     public void Start()
00186     {
00187         _shellTask = Task.Run(async () =>
00188         {
00189             var bufferStdIn = new StringBuilder("abc");
00190             var bufferStdOut = new StringBuilder();
00191             var bufferStdError = new StringBuilder();
00192         
00193             try
00194             {
00195                 var terminalCommandRequest = new TerminalCommandRequest(
00196                     $"{_pathToShellExecutable} -i",
00197                     _environmentProvider.HomeDirectoryAbsolutePath.Value);
00198                 
00199                 var parsedCommand = await TerminalInteractive.TryHandleCommand(terminalCommandRequest);
00200                 ActiveTerminalCommandParsed = parsedCommand;
00201         
00202                 if (parsedCommand is null)
00203                     return;
00204                     
00205                 TerminalOutput.WriteOutput(
00206                     parsedCommand,
00207                     new StartedCommandEvent(-1));
00208                     
00209                 var pipeFilePath = _environmentProvider.JoinPaths(
00210                     _environmentProvider.SafeRoamingApplicationDataDirectoryAbsolutePath.Value,
00211                     "terminal-test.txt");
00212                     
00213                 _shellCliWrapCommand = "abc" | Cli
00214                     .Wrap(parsedCommand.TargetFileName)
00215                     .WithWorkingDirectory(terminalCommandRequest.WorkingDirectory) |
00216                     (PipeTarget.ToStringBuilder(bufferStdOut), PipeTarget.ToStringBuilder(bufferStdError));
00217         
00218                 if (!string.IsNullOrWhiteSpace(parsedCommand.Arguments))
00219                     _shellCliWrapCommand = _shellCliWrapCommand.WithArguments(parsedCommand.Arguments);
00220             
00221                 Console.WriteLine("before shell");
00222                 
00223                 await _shellCliWrapCommand
00224                     .ExecuteAsync(_commandCancellationTokenSource.Token);
00225                     
00226                 Console.WriteLine("after shell");
00227             }
00228             catch (Exception e)
00229             {
00230                 Console.WriteLine("exception shell");
00231                 NotificationHelper.DispatchError("Terminal Exception", e.ToString(), _commonComponentRenderers, _dispatcher, TimeSpan.FromSeconds(14));
00232             }
00233             finally
00234             {
00235                 Console.WriteLine($"bufferStdOut: {bufferStdOut}");
00236                 Console.WriteLine($"bufferStdError: {bufferStdError}");
00237             }
00238         });
00239     }
