00059     private string CommandUiInputBinding
00060     {
00061         get => _command;
00062         set
00063         {
00064             _command = value;
00065             
00066             // If not browsing history, cache what the user is typing
00067             //
00068             // This means that while the '_cachedCommand' protects against
00069             // accidental pressing of the ArrowUp key,
00070             //
00071             // If one is modifying a command that was populated into the
00072             // input element by browsing history,
00073             // then until the enter key is pressed to stop browsing history,
00074             // nothing will be cached and an accidental ArrowUp here could lose information.
00075             //
00076             // But, the priority for protecting against lost information by accidental ArrowUp
00077             // is on an original newly being typed command, not the history.
00078             if (_terminalCommandRequestHistory is null)
00079                 _cachedCommand = value;
00080         }
00081     }
