00083     protected override void OnParametersSet()
00084     {
00085         var nextTerminal = Terminal;
00086         
00087         if (_previousTerminal is null ||
00088             _previousTerminal.Key != nextTerminal.Key)
00089         {
00090             if (_previousTerminal is not null)
00091             {
00092                 _previousTerminal.TerminalInteractive.WorkingDirectoryChanged -= OnWorkingDirectoryChanged;
00093                 _previousTerminal.TerminalOutput.OnWriteOutput -= OnWriteOutput;
00094             }
00095             
00096             if (nextTerminal is not null)
00097             {
00098                 nextTerminal.TerminalInteractive.WorkingDirectoryChanged += OnWorkingDirectoryChanged;
00099                 nextTerminal.TerminalOutput.OnWriteOutput += OnWriteOutput;
00100             }
00101             
00102             // TODO: Is it possible for the Dispose() method to be invoked prior to...
00103             //       ...OnParametersSet() finishing?
00104             //       |
00105             //       It is being presumed that 'Dispose()' will not fire until 'OnParametersSet()'
00106             //       finishes. But, this should be proven to be the case.
00107             _previousTerminal = nextTerminal;
00108             
00109             // The name of the method 'OnWriteOutput()' is awkward.
00110             // The invocation here is to reload the text since the terminal changed.
00111             OnWriteOutput();
00112         }
00113         
00114         base.OnParametersSet();
00115     }
