00117     private TerminalCommandRequest? GetTerminalCommandRequestAtIndexHistory(
00118         int indexLocal,
00119         ImmutableList<TerminalCommandRequest> historyLocal)
00120     {
00121         if (indexLocal < historyLocal.Count)
00122             return historyLocal[indexLocal];
00123             
00124         return null;
