00127     private void HandleOnKeyDown(KeyboardEventArgs keyboardEventArgs)
00128     {
00129         if (keyboardEventArgs.Code == "Enter")
00130         {
00131             var commandLocal = _command;
00132             _terminalCommandRequestHistory = null;
00133             _indexHistory = 0;
00134         
00135             Terminal.EnqueueCommand(new TerminalCommandRequest(
00136                 commandText: commandLocal,
00137                 workingDirectory: null));
00138         }
00139         else if (keyboardEventArgs.Key == "ArrowUp")
00140         {
00141             if (_terminalCommandRequestHistory is null)
00142             {
00143                 _terminalCommandRequestHistory = Terminal.TerminalInteractive.GetTerminalCommandRequestHistory();
00144                 _indexHistory = 0;
00145                 
00146                 var commandAtIndex = GetTerminalCommandRequestAtIndexHistory(
00147                     _indexHistory,
00148                     _terminalCommandRequestHistory);
00149                     
00150                 if (commandAtIndex is not null)
00151                     _command = commandAtIndex.CommandText;
00152             }
00153             else
00154             {
00155                 if (_indexHistory < _terminalCommandRequestHistory.Count - 1)
00156                     _indexHistory++;
00157                     
00158                 var commandAtIndex = GetTerminalCommandRequestAtIndexHistory(
00159                     _indexHistory,
00160                     _terminalCommandRequestHistory);
00161                     
00162                 if (commandAtIndex is not null)
00163                     _command = commandAtIndex.CommandText;
00164             }
00165         }
00166         else if (keyboardEventArgs.Key == "ArrowDown")
00167         {
00168             if (_terminalCommandRequestHistory is not null)
00169             {
00170                 if (_indexHistory == 0)
00171                 {
00172                     _command = _cachedCommand;
00173                     _terminalCommandRequestHistory = null;
00174                 }
00175                 else
00176                 {
00177                     if (_indexHistory < 0)
00178                         _indexHistory = 0;
00179                     else
00180                         _indexHistory--;
00181                         
00182                     var commandAtIndex = GetTerminalCommandRequestAtIndexHistory(
00183                         _indexHistory,
00184                         _terminalCommandRequestHistory);
00185                         
00186                     if (commandAtIndex is not null)
00187                         _command = commandAtIndex.CommandText;
00188                 }
00189             }
00190         }
00191     }
