00198     private void OnWriteOutput()
00199     {
00200         _throttle.Run(_ =>
00201         {
00202             TextEditorService.PostUnique(
00203                 nameof(TerminalOutput),
00204                 editContext =>
00205                 {
00206                     var formatter = Terminal.TerminalOutput.OutputFormatterList.FirstOrDefault(
00207                         x => x.Name == nameof(TerminalOutputFormatterExpand));
00208                         
00209                     if (formatter is not TerminalOutputFormatterExpand terminalOutputFormatterExpand)
00210                         return Task.CompletedTask;
00211                     
00212                     var modelModifier = editContext.GetModelModifier(terminalOutputFormatterExpand.TextEditorModelResourceUri);
00213                     var viewModelModifier = editContext.GetViewModelModifier(terminalOutputFormatterExpand.TextEditorViewModelKey);
00214                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00215                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00216 
00217                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00218                         return Task.CompletedTask;
00219 
00220                     var localTerminal = Terminal;
00221 
00222                     var outputFormatted = (TerminalOutputFormattedTextEditor)localTerminal.TerminalOutput
00223                         .GetOutputFormatted(nameof(TerminalOutputFormatterExpand));
00224                     
00225                     modelModifier.SetContent(outputFormatted.Text);
00226                     
00227                     var lineIndexOriginal = primaryCursorModifier.LineIndex;
00228                     var columnIndexOriginal = primaryCursorModifier.ColumnIndex;
00229                     
00230                     // Move Cursor, try to preserve the current cursor position.
00231                     {
00232                         if (primaryCursorModifier.LineIndex > modelModifier.LineCount - 1)
00233                             primaryCursorModifier.LineIndex = modelModifier.LineCount - 1;
00234                         
00235                         var lineInformation = modelModifier.GetLineInformation(primaryCursorModifier.LineIndex);
00236                         
00237                         if (primaryCursorModifier.ColumnIndex > lineInformation.LastValidColumnIndex)
00238                             primaryCursorModifier.SetColumnIndexAndPreferred(lineInformation.LastValidColumnIndex);
00239                             
00240                         if (lineIndexOriginal != primaryCursorModifier.LineIndex ||
00241                             columnIndexOriginal != primaryCursorModifier.ColumnIndex)
00242                         {
00243                             viewModelModifier.ViewModel.UnsafeState.ShouldRevealCursor = true;
00244                         }
00245                     }
00246                     
00247                     var compilerServiceResource = modelModifier.CompilerService.GetCompilerServiceResourceFor(
00248                         terminalOutputFormatterExpand.TextEditorModelResourceUri);
00249 
00250                     if (compilerServiceResource is TerminalResource terminalResource)
00251                     {                   
00252                         terminalResource.ManualDecorationTextSpanList.Clear();
00253                         terminalResource.ManualDecorationTextSpanList.AddRange(
00254                             outputFormatted.SymbolList.Select(x => x.TextSpan));
00255                                 
00256                         terminalResource.ManualSymbolList.Clear();
00257                         terminalResource.ManualSymbolList.AddRange(outputFormatted.SymbolList);
00258 
00259                         editContext.TextEditorService.ModelApi.ApplyDecorationRange(
00260                             editContext,
00261                             modelModifier,
00262                             terminalResource.GetTokenTextSpans());
00263                             
00264                             editContext.TextEditorService.ModelApi.ApplyDecorationRange(
00265                             editContext,
00266                             modelModifier,
00267                             outputFormatted.TextSpanList);
00268                     }
00269                     
00270                     return Task.CompletedTask;
00271                 });
00272             return Task.CompletedTask;
00273         });
00274     }
