00076     public void ClearFireAndForget()
00077     {
00078         var localHasExecutingProcess = HasExecutingProcess;
00079     
00080         _ = Task.Run(() =>
00081         {
00082             if (localHasExecutingProcess)
00083             {
00084                 TerminalOutput.ClearOutputExceptMostRecentCommand();
00085             }
00086             else
00087             {
00088                 TerminalOutput.ClearOutput();
00089             }
00090             
00091             return Task.CompletedTask;
00092         });
00093     }
