00095     private async Task HandleCommand(TerminalCommandRequest terminalCommandRequest)
00096     {
00097         TerminalOutput.ClearHistoryWhenExistingOutputTooLong();
00098     
00099         var parsedCommand = await TerminalInteractive.TryHandleCommand(terminalCommandRequest);
00100         ActiveTerminalCommandParsed = parsedCommand;
00101 
00102         if (parsedCommand is null)
00103             return;
00104         
00105         try
00106         {
00107             HasExecutingProcess = true;
00108         
00109             if (parsedCommand.SourceTerminalCommandRequest.BeginWithFunc is not null)
00110             {
00111                 await parsedCommand.SourceTerminalCommandRequest.BeginWithFunc
00112                     .Invoke(parsedCommand)
00113                     .ConfigureAwait(false);
00114             }
00115             
00116             await ExecuteWebsiteCliAsync(parsedCommand, HandleOutput, _commandCancellationTokenSource.Token)
00117                 .ConfigureAwait(false);
00118         }
00119         catch (Exception e)
00120         {
00121             // TODO: This will erroneously write 'StartedCommandEvent' out twice...
00122             //       ...unless a check is added to see WHEN the exception was thrown.
00123             TerminalOutput.WriteOutput(parsedCommand, new StartedCommandEvent(-1));
00124         
00125             TerminalOutput.WriteOutput(
00126                 parsedCommand,
00127                 new StandardErrorCommandEvent(parsedCommand.SourceTerminalCommandRequest.CommandText + " threw an exception" + "\n"));
00128         
00129             NotificationHelper.DispatchError("Terminal Exception", e.ToString(), _commonComponentRenderers, _dispatcher, TimeSpan.FromSeconds(14));
00130         }
00131         finally
00132         {
00133             if (parsedCommand.SourceTerminalCommandRequest.ContinueWithFunc is not null)
00134             {
00135                 await parsedCommand.SourceTerminalCommandRequest.ContinueWithFunc
00136                     .Invoke(parsedCommand)
00137                     .ConfigureAwait(false);
00138             }
00139         
00140             HasExecutingProcess = false;
00141         }
00142     }
00143     
