00184     private Task DotNetWebsiteCliAsync(
00185         TerminalCommandParsed parsedCommand,
00186         Action<CommandEvent> handleOutputAction,
00187         CancellationToken cancellationToken)
00188     {
00189         var argumentList = parsedCommand.Arguments.Trim().Split(' ');
00190     
00191         var firstArgument = argumentList.FirstOrDefault();
00192         
00193         if (string.IsNullOrWhiteSpace(firstArgument))
00194         {
00195             TerminalOutput.WriteOutput(parsedCommand, new StandardErrorCommandEvent($"firstArgument was null or whitespace."));
00196         }
00197     
00198         switch (firstArgument)
00199         {
00200             case TerminalWebsiteFacts.InitialArguments.RUN:
00201             {
00202                 var projectPath = (string?)null;
00203             
00204                 if (argumentList.Length == 2)
00205                 {
00206                     projectPath = argumentList[1];
00207                 }
00208                 else if (argumentList.Length == 3)
00209                 {
00210                     if (argumentList[1] != TerminalWebsiteFacts.Options.PROJECT)
00211                     {
00212                         TerminalOutput.WriteOutput(parsedCommand, new StandardErrorCommandEvent($"argumentList[1]:{argumentList[1]} != {TerminalWebsiteFacts.Options.PROJECT}"));
00213                         return Task.CompletedTask;
00214                     }
00215                 
00216                     projectPath = argumentList[2];
00217                 }
00218                 else
00219                 {
00220                     TerminalOutput.WriteOutput(parsedCommand, new StandardErrorCommandEvent($"Bad argument count: '{argumentList.Length}'."));
00221                     return Task.CompletedTask;
00222                 }
00223             
00224                 TerminalOutput.WriteOutput(parsedCommand, new StandardErrorCommandEvent($"projectPath:{projectPath}"));
00225                 return Task.CompletedTask;
00226             }
00227             default:
00228             {
00229                 TerminalOutput.WriteOutput(parsedCommand, new StandardErrorCommandEvent($"First argument: '{firstArgument}' was not recognized."));
00230                 return Task.CompletedTask;
00231             }
00232         }
00233         
00234         throw new LuthetusIdeException($"The method '{nameof(DotNetWebsiteCliAsync)}' should return on each branch of the switch statement.");
00235     }
