00053     private async Task UpdateContent()
00054     {
00055         var newContent = string.Empty;
00056         var newDecorationTextSpanList = new List<TextEditorTextSpan>();
00057         var textOffset = 0;
00058 
00059         if (RenderBatch.TreeViewContainer.SelectedNodeList.Count > 1)
00060         {
00061             var newContentBuilder = new StringBuilder();
