00070                 newContentBuilder.Append(subContent);
00071 
00072                 if (i != RenderBatch.TreeViewContainer.SelectedNodeList.Count - 1)
00073                 {
00074                     var spacingBetweenEntries = "\n\n==================================================\n\n";
00075                     newContentBuilder.Append(spacingBetweenEntries);
00076 
00077                     // Decoration text span
00078                     {
00079                         var startPositionInclusive = textOffset;
00080                         var endPositionExclusive = textOffset + spacingBetweenEntries.Length;
00081 
00082                         // TODO: Bad idea to use string.Empty here as the source text for the text span...
00083                         //       ...If one invokes '.GetText()' this will throw and index out of bounds exception.
00084                         //       |
