00091                             ResourceUriFacts.TestExplorerDetailsTextEditorResourceUri,
00092                             string.Empty));
00093                     }
00094 
00095                     textOffset += spacingBetweenEntries.Length;
00096                 }
00097             }
00098 
00099             newContent = newContentBuilder.ToString();
00100         }
00101         else
00102         {
00103             newContent = await GetNodeContent(
00104                 RenderBatch.TreeViewContainer.SelectedNodeList.Single(),
00105                 newDecorationTextSpanList,
00106                 textOffset);
00107         }
00108 
00109         if (newContent != _previousContent)
00110         {
00111             _previousContent = newContent;
00112 
00113             for (int i = 0; i < newDecorationTextSpanList.Count; i++)
00114             {
00115                 var textSpan = newDecorationTextSpanList[i];
00116                 newDecorationTextSpanList[i] = textSpan with
