00019     public static KeymapArgsKind GetKeymapArgsKind(
00020         TextEditorComponentData componentData,
00021         KeymapArgs keymapArgs,
00022         bool hasSelection,
00023         ITextEditorService textEditorService,
00024         out CommandNoType command)
00025     {
00026         var eventIsCommand = CheckIfKeymapArgsMapsToCommand(
00027             componentData,
00028             keymapArgs,
00029             hasSelection,
00030             textEditorService,
00031             out Key<KeymapLayer> layerKey,
00032             out var success,
00033             out command);
00034 
00035         var eventIsMovement = CheckIfKeyboardEventArgsMapsToMovement(keymapArgs, command);
00036 
00037         var eventIsContextMenu = KeyboardKeyFacts.CheckIsContextMenuEvent(keymapArgs);
00038 
00039         if (eventIsMovement)
00040             return KeymapArgsKind.Movement;
00041 
00042         if (eventIsContextMenu)
00043             return KeymapArgsKind.ContextMenu;
00044 
00045         if (eventIsCommand)
00046             return KeymapArgsKind.Command;
00047 
00048         if (keymapArgs.Key.Length == 1)
00049         {
00050             // Only write text if no modifiers (other than shift) were held at the time of the event.
00051             if (!keymapArgs.CtrlKey && !keymapArgs.AltKey && !keymapArgs.MetaKey)
00052                 return KeymapArgsKind.Text;
00053             else
00054                 return KeymapArgsKind.None;
00055         }
00056 
00057         return KeymapArgsKind.Other;
00058     }
