00124     public static bool CheckIfKeymapArgsMapsToCommand(
00125         TextEditorComponentData componentData,
00126         KeymapArgs keymapArgs,
00127         bool hasSelection,
00128         ITextEditorService textEditorService,
00129         out Key<KeymapLayer> layerKey,
00130         out bool success,
00131         out CommandNoType command)
00132     {
00133         layerKey = ((ITextEditorKeymap)componentData.Options.Keymap!).GetLayer(hasSelection);
00134 
00135         keymapArgs.LayerKey = layerKey;
00136 
00137         success = ((ITextEditorKeymap)componentData.Options.Keymap!).TryMap(
00138             keymapArgs,
00139             componentData,
00140             out command);
00141 
00142         if (!success && keymapArgs.LayerKey != TextEditorKeymapDefaultFacts.DefaultLayer.Key)
00143         {
00144             keymapArgs.LayerKey = TextEditorKeymapDefaultFacts.DefaultLayer.Key;
00145 
00146             _ = ((ITextEditorKeymap)componentData.Options.Keymap!).TryMap(
00147                 keymapArgs,
00148                 componentData,
00149                 out command);
00150         }
00151 
00152         if (KeyboardKeyFacts.WhitespaceCodes.ENTER_CODE == keymapArgs.Code && keymapArgs.ShiftKey)
00153             command = TextEditorCommandDefaultFacts.NewLineBelow;
00154 
00155         return command is not null;
00156     }
