00158     public static bool CheckIfKeyboardEventArgsMapsToMovement(KeymapArgs keymapArgs, CommandNoType command)
00159     {
00160         return KeyboardKeyFacts.IsMovementKey(keymapArgs.Key) && command is null;
00161     }
