00163     public static bool IsAutocompleteMenuInvoker(KeymapArgs keymapArgs)
00164     {
00165         // LetterOrDigit was hit without Ctrl being held
00166         return !keymapArgs.CtrlKey &&
00167                !KeyboardKeyFacts.IsWhitespaceCode(keymapArgs.Code) &&
00168                !KeyboardKeyFacts.IsMetaKey(keymapArgs);
00169     }
00170 
00171     public static bool IsSyntaxHighlightingInvoker(KeymapArgs keymapArgs)
00172     {
00173         return keymapArgs.Key == ";" ||
00174                KeyboardKeyFacts.IsWhitespaceCode(keymapArgs.Code) ||
00175                keymapArgs.CtrlKey && keymapArgs.Key == "s" ||
00176                keymapArgs.CtrlKey && keymapArgs.Key == "v" ||
00177                keymapArgs.CtrlKey && keymapArgs.Key == "z" ||
00178                keymapArgs.CtrlKey && keymapArgs.Key == "y";
00179     }
00180 
00186     public static bool IsAutocompleteIndexerInvoker(KeymapArgs keymapArgs)
00187     {
00188         return (KeyboardKeyFacts.IsWhitespaceCode(keymapArgs.Code) ||
00189                     KeyboardKeyFacts.IsPunctuationCharacter(keymapArgs.Key.First())) &&
00190                 !keymapArgs.CtrlKey;
00191     }
