00193     public static async Task<(int rowIndex, int columnIndex)> CalculateRowAndColumnIndex(
00194         ResourceUri resourceUri,
00195         Key<TextEditorViewModel> viewModelKey,
00196         MouseEventArgs mouseEventArgs,
00197         TextEditorComponentData componentData,
00198         ITextEditorEditContext editContext)
00199     {
00200         var modelModifier = editContext.GetModelModifier(resourceUri);
00201         var viewModelModifier = editContext.GetViewModelModifier(viewModelKey);
00202         var globalTextEditorOptions = editContext.TextEditorService.OptionsStateWrap.Value.Options;
00203 
00204         if (modelModifier is null || viewModelModifier is null)
00205             return (0, 0);
00206 
00207         var charMeasurements = viewModelModifier.ViewModel.CharAndLineMeasurements;
00208         var textEditorDimensions = viewModelModifier.ViewModel.TextEditorDimensions;
00209         var scrollbarDimensions = viewModelModifier.ViewModel.ScrollbarDimensions;
00210 
00211         var relativeX = mouseEventArgs.ClientX - textEditorDimensions.BoundingClientRectLeft;
00212         var relativeY = mouseEventArgs.ClientY - textEditorDimensions.BoundingClientRectTop;
00213 
00214         var positionX = relativeX;
00215         var positionY = relativeY;
00216 
00217         // Scroll position offset
00218         {
00219             positionX += scrollbarDimensions.ScrollLeft;
00220             positionY += scrollbarDimensions.ScrollTop;
00221         }
00222 
00223         var rowIndex = (int)(positionY / charMeasurements.LineHeight);
00224 
00225         rowIndex = rowIndex > modelModifier.LineCount - 1
00226             ? modelModifier.LineCount - 1
00227             : rowIndex;
00228 
00229         int columnIndexInt;
00230 
00231 
00232         var columnIndexDouble = positionX / charMeasurements.CharacterWidth;
00233         columnIndexInt = (int)Math.Round(columnIndexDouble, MidpointRounding.AwayFromZero);
00234 
00235         var lengthOfRow = modelModifier.GetLineLength(rowIndex);
00236 
00237         // Tab key column offset
00238         {
00239             var parameterForGetTabsCountOnSameRowBeforeCursor = columnIndexInt > lengthOfRow
00240                 ? lengthOfRow
00241                 : columnIndexInt;
00242 
00243             int tabsOnSameRowBeforeCursor;
00244 
00245             try
00246             {
00247                 tabsOnSameRowBeforeCursor = modelModifier.GetTabCountOnSameLineBeforeCursor(
00248                     rowIndex,
00249                     parameterForGetTabsCountOnSameRowBeforeCursor);
00250             }
00251             catch (LuthetusTextEditorException)
00252             {
00253                 tabsOnSameRowBeforeCursor = 0;
00254             }
00255 
00256             // 1 of the character width is already accounted for
00257             var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
00258 
00259             columnIndexInt -= extraWidthPerTabKey * tabsOnSameRowBeforeCursor;
00260         }
00261 
00262         columnIndexInt = columnIndexInt > lengthOfRow
00263             ? lengthOfRow
00264             : columnIndexInt;
00265 
00266         rowIndex = Math.Max(rowIndex, 0);
00267         columnIndexInt = Math.Max(columnIndexInt, 0);
00268 
00269         return (rowIndex, columnIndexInt);
00270     }
