00035     [Fact]
00036     public async Task Copy()
00037     {
00038         InitializeTextEditorCommandDefaultFactsTests(
00039             out var textEditorService, out var inModel, out var inViewModel,
00040             out var textEditorCommandArgs, out var serviceProvider);
00041 
00042         var clipboardService = serviceProvider.GetRequiredService<IClipboardService>();
00043 
00044         // No selection
00045         {
00046             await clipboardService.SetClipboard(string.Empty);
00047             var inClipboard = await clipboardService.ReadClipboard();
00048             Assert.Empty(inClipboard);
00049 
00050             await TextEditorCommandDefaultFacts.Copy.CommandFunc.Invoke(textEditorCommandArgs);
00051 
00052             var outClipboard = await clipboardService.ReadClipboard();
00053             Assert.Equal("Hello World!\n", outClipboard);
00054         }
00055 
00056 
00057         // With selection
00058         {
00059             await clipboardService.SetClipboard(string.Empty);
00060             var inClipboard = await clipboardService.ReadClipboard();
00061             Assert.Empty(inClipboard);
00062 
00063             textEditorService.PostUnique(
00064                 nameof(TextEditorCommandDefaultFactsTests),
00065                 editContext =>
00066                 {
00067                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
00068                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
00069                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00070                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00071 
00072                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00073                         return Task.CompletedTask;
00074 
00075                     primaryCursorModifier.LineIndex = 1;
00076                     primaryCursorModifier.SetColumnIndexAndPreferred(9);
00077                     primaryCursorModifier.SelectionAnchorPositionIndex = 15;
00078                     primaryCursorModifier.SelectionEndingPositionIndex = 22;
00079 
00080                     return Task.CompletedTask;
00081                 });
00082 
00083             await TextEditorCommandDefaultFacts.Copy.CommandFunc.Invoke(textEditorCommandArgs);
00084 
00085             var outClipboard = await clipboardService.ReadClipboard();
00086             Assert.Equal("Pillows", outClipboard);
00087         }
00088     }
