00275     public async Task Save()
00276     {
00277         // ViewModel.OnSaveRequested is NOT null
00278         {
00279             InitializeTextEditorCommandDefaultFactsTests(
00280                 out var textEditorService, out var inModel, out var inViewModel,
00281                 out var textEditorCommandArgs, out var serviceProvider);
00282 
00283             var savedContent = (string?)null;
00284             Assert.Null(savedContent);
00285 
00286             textEditorService.PostUnique(
00287                 nameof(TextEditorCommandDefaultFactsTests),
00288                 editContext =>
00289                 {
00290                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
00291                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
00292                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00293                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00294 
00295                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00296                         return Task.CompletedTask;
00297 
00298                     viewModelModifier.ViewModel = viewModelModifier.ViewModel with
00299                     {
00300                         OnSaveRequested = model => savedContent = model.GetAllText()
00301                     };
00302 
00303                     return Task.CompletedTask;
00304                 });
00305 
00306             await TextEditorCommandDefaultFacts.TriggerSave.CommandFunc.Invoke(textEditorCommandArgs);
00307 
00308             textEditorService.PostUnique(
00309                 nameof(TextEditorCommandDefaultFactsTests),
00310                 editContext =>
00311                 {
00312                     Assert.Equal(TestConstants.SOURCE_TEXT, savedContent);
00313                     return Task.CompletedTask;
00314                 });
00315         }
00316 
00317         // ViewModel.OnSaveRequested is null
00318         {
00319                 InitializeTextEditorCommandDefaultFactsTests(
00320                 out var textEditorService, out var inModel, out var inViewModel,
00321                 out var textEditorCommandArgs, out var serviceProvider);
00322 
00323                 var savedContent = (string?)null;
00324 
00325                 Assert.Null(savedContent);
00326                 await TextEditorCommandDefaultFacts.TriggerSave.CommandFunc.Invoke(textEditorCommandArgs);
00327 
00328             textEditorService.PostUnique(
00329                     nameof(TextEditorCommandDefaultFactsTests),
00330                     editContext =>
00331                     {
00332                         Assert.Null(savedContent);
00333                         return Task.CompletedTask;
00334                     });
00335         }
