00341     [Fact]
00342     public async Task SelectAll()
00343     {
00344         // No already existing selection
00345         {
00346             InitializeTextEditorCommandDefaultFactsTests(
00347                 out var textEditorService, out var inModel, out var inViewModel,
00348                 out var textEditorCommandArgs, out var serviceProvider);
00349 
00350             await TextEditorCommandDefaultFacts.SelectAll.CommandFunc.Invoke(textEditorCommandArgs);
00351 
00352             var outViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
00353             Assert.NotNull(outViewModel);
00354 
00355             Assert.Equal(0, outViewModel!.PrimaryCursor.Selection.AnchorPositionIndex);
00356             Assert.Equal(inModel.DocumentLength, outViewModel!.PrimaryCursor.Selection.EndingPositionIndex);
00357 
00358             // Assert that 'SelectAll' does not move the cursor itself, it only should move the selection
00359             {
00360                 Assert.Equal(inViewModel.PrimaryCursor.LineIndex, outViewModel!.PrimaryCursor.LineIndex);
00361                 Assert.Equal(inViewModel.PrimaryCursor.ColumnIndex, outViewModel!.PrimaryCursor.ColumnIndex);
00362             }
00363         }
00364 
00365         // With an already existing selection
00366         {
00367             InitializeTextEditorCommandDefaultFactsTests(
00368                 out var textEditorService, out var inModel, out var inViewModel,
00369                 out var textEditorCommandArgs, out var serviceProvider);
00370 
00371             textEditorService.PostUnique(
00372                 nameof(TextEditorCommandDefaultFactsTests),
00373                 editContext =>
00374                 {
00375                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
00376                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
00377                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00378                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00379 
00380                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00381                         return Task.CompletedTask;
00382 
00383                     // Select the "Hello" text on the first row
00384                     {
00385                         primaryCursorModifier.SelectionAnchorPositionIndex = 0;
00386                         primaryCursorModifier.SelectionEndingPositionIndex = 5;
00387                     }
00388 
00389                     // SetColumnIndexAndPreferred is unnecessary, but the user is likely
00390                     // to have their ColumnIndex == SelectionEndingPositionIndex
00391                     primaryCursorModifier.SetColumnIndexAndPreferred(
00392                         primaryCursorModifier.SelectionEndingPositionIndex);
00393 
00394                     return Task.CompletedTask;
00395                 });
00396 
00397             await TextEditorCommandDefaultFacts.SelectAll.CommandFunc.Invoke(textEditorCommandArgs);
00398 
00399             var outViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
00400             Assert.NotNull(outViewModel);
00401 
00402             Assert.Equal(0, outViewModel!.PrimaryCursor.Selection.AnchorPositionIndex);
00403             Assert.Equal(inModel.DocumentLength, outViewModel!.PrimaryCursor.Selection.EndingPositionIndex);
00404         }
00405     }
