00410     [Fact]
00411     public async Task Undo()
00412     {
00413         // Able to undo
00414         {
00415             InitializeTextEditorCommandDefaultFactsTests(
00416                 out var textEditorService, out var inModel, out var inViewModel,
00417                 out var textEditorCommandArgs, out var serviceProvider);
00418 
00419             // Modify text, as to create an edit which one can then undo
00420             {
00421                 var cursor = new TextEditorCursor(0, 0, true);
00422 
00423                 var cursorModificationBag = new CursorModifierBagTextEditor(
00424                     Key<TextEditorViewModel>.Empty,
00425                     new List<TextEditorCursorModifier> { new TextEditorCursorModifier(cursor) });
00426 
00427                 textEditorService.PostUnique(
00428                     nameof(TextEditorCommandDefaultFactsTests),
00429                     editContext =>
00430                     {
00431                         var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
00432 
00433                         textEditorService.ModelApi.InsertTextUnsafe(
00434                             editContext,
00435                             modelModifier,
00436                             cursorModificationBag,
00437                             "zyx",
00438                             CancellationToken.None);
00439                         return Task.CompletedTask;
00440                     });
00441             }
00442 
00443 
00444             // Assert that the new text is different from the original
00445             {
00446                 var refModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
00447                 Assert.NotNull(refModel);
00448 
00449                 var inText = inModel.GetAllText();
00450                 var refText = refModel!.GetAllText();
00451                 Assert.NotEqual(inText, refText);
00452             }
00453             
00454             // Invoke the command
00455             await TextEditorCommandDefaultFacts.Undo.CommandFunc.Invoke(textEditorCommandArgs);
00456 
00457             // Assert that the text was reverted to the original
00458             {
00459                 var outModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
00460                 Assert.NotNull(outModel);
00461 
00462                 var inText = inModel.GetAllText();
00463                 var outText = outModel!.GetAllText();
00464                 Assert.Equal(inText, outText);
00465             }
00466         }
00467 
00468         // Cannot undo
00469         {
00470             InitializeTextEditorCommandDefaultFactsTests(
00471                 out var textEditorService, out var inModel, out var inViewModel,
00472                 out var textEditorCommandArgs, out var serviceProvider);
00473 
00474             // Capture the current text, as to later be used after the
00475             // Undo invocation to Assert the text had not changed.
00476             var refModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
00477             Assert.NotNull(refModel);
00478             var refText = refModel!.GetAllText();
00479 
00480             // Invoke the command
00481             await TextEditorCommandDefaultFacts.Undo.CommandFunc.Invoke(textEditorCommandArgs);
00482 
00483             // Capture the text, now that the Undo command was invoked.
00484             var outModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
00485             Assert.NotNull(outModel);
00486             var outText = outModel!.GetAllText();
00487 
00488             // Assert that the text immediately BEFORE invoking the 'Undo' command
00489             // is equal to the text which one gets AFTER invoking 'Undo'
00490             //
00491             // This asserts that the 'cannot undo' case does not modify the text in any way.
00492             Assert.Equal(refText, outText);
00493         }
00494     }
