00643     [Fact]
00644     public async Task IndentMore()
00645     {
00646         // Invoke IndentMore on 1 row, by selecting a single character,
00647         // where the character is NOT at the start or end of the row
00648         //
00649         // Reasoning: In Luthetus.Ide, this should IndentMore the line on which the selection lies.
00650         //            This behavior is not universal however.
00651         //            In Visual Studio this will delete the user's selected text, and
00652         //            then insert a single tab character.
00653         //            
00654         {
00655             InitializeTextEditorCommandDefaultFactsTests(
00656                 out var textEditorService, out var inModel, out var inViewModel,
00657                 out var textEditorCommandArgs, out var serviceProvider);
00658 
00659             textEditorService.PostUnique(
00660                 nameof(TextEditorCommandDefaultFactsTests),
00661                 editContext =>
00662                 {
00663                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
00664                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
00665                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00666                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00667 
00668                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
00669                         return Task.CompletedTask;
00670 
00671                     primaryCursorModifier.LineIndex = 1;
00672                     primaryCursorModifier.SetColumnIndexAndPreferred(3);
00673                     primaryCursorModifier.SelectionAnchorPositionIndex = 15;
00674                     primaryCursorModifier.SelectionEndingPositionIndex = 16;
00675 
00676                     // Assert that the text selected, is what was planned
00677                     {
00678                         var selectedText = TextEditorSelectionHelper.GetSelectedText(
00679                             primaryCursorModifier,
00680                             modelModifier);
00681 
00682                         Assert.Equal("P", selectedText);
00683                     }
00684 
00685                     return Task.CompletedTask;
00686                 });
00687 
00688             await TextEditorCommandDefaultFacts.IndentMore.CommandFunc.Invoke(textEditorCommandArgs);
00689 
00690             // Assert that the row(s) were indented: "7 Pillows\n" -> "\t7 Pillows\n"
00691             {
00692                 var outModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
00693                 Assert.NotNull(outModel);
00694 
00695                 var textOnRow = new string(outModel!
00696                     .GetLineRichCharacterRange(1, 1)
00697                     .Single()
00698                     .Select(x => x.Value)
00699                     .ToArray());
00700 
00701                 Assert.Equal("\t7 Pillows\n", textOnRow);
00702             }
00703 
00704             // Assert that the viewModel's cursor was moved properly (including its selection)
00705             {
00706                 var outViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
00707                 Assert.NotNull(outViewModel);
00708                 var primaryCursor = outViewModel!.PrimaryCursor;
00709 
00710                 // The row index should be unchanged
00711                 Assert.Equal(1, primaryCursor.LineIndex);
00712 
00713                 Assert.Equal(4, primaryCursor.ColumnIndex);
00714                 // Assert column and preferred indices to ensure both were set,
00715                 // as opposed to only ColumnIndex and forgetting to also change PreferredColumnIndex.
00716                 Assert.Equal(primaryCursor.ColumnIndex, primaryCursor.PreferredColumnIndex);
00717 
00718                 // Due to the insertion of a '\t' character, the selection should move 1 character further
00719                 Assert.Equal(16, primaryCursor.Selection.AnchorPositionIndex);
00720                 Assert.Equal(17, primaryCursor.Selection.EndingPositionIndex);
00721             }
00722         }
00723 
