00982     [Fact]
00983     public async Task IndentLess()
00984     {
00985         // Invoke IndexLess on a single line of which the line has one tab character at the start.
00986         // Only select a single character.
00987         {
00988             InitializeTextEditorCommandDefaultFactsTests(
00989                 out var textEditorService, out var inModel, out var inViewModel,
00990                 out var textEditorCommandArgs, out var serviceProvider);
00991 
00992             textEditorService.PostUnique(
00993                 nameof(TextEditorCommandDefaultFactsTests),
00994                 async editContext =>
00995                 {
00996                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
00997                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
00998                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
00999                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
01000 
01001                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
01002                         return;
01003 
01004                     // Insert a tab character at the start of the row under testing
01005                     {
01006                         var cursor = new TextEditorCursor(1, 0, true);
01007                         var cursorModifier = new TextEditorCursorModifier(cursor);
01008 
01009                         var insertionCursorModifierBag = new CursorModifierBagTextEditor(
01010                             Key<TextEditorViewModel>.Empty,
01011                             new List<TextEditorCursorModifier> { cursorModifier });
01012 
01013                         textEditorService.ModelApi.InsertTextUnsafe(
01014                             editContext,
01015                             modelModifier,
01016                             insertionCursorModifierBag,
01017                             "\t",
01018                             CancellationToken.None);
01019 
01020                         var rowText = new string(
01021                             modelModifier.GetLineRichCharacterRange(1, 1).Single().Select(x => x.Value).ToArray());
01022 
01023                         Assert.Equal("\t7 Pillows\n", rowText);
01024                     }
01025 
01026                     primaryCursorModifier.LineIndex = 1;
01027                     primaryCursorModifier.SetColumnIndexAndPreferred(4);
01028                     primaryCursorModifier.SelectionAnchorPositionIndex = 16;
01029                     primaryCursorModifier.SelectionEndingPositionIndex = 17;
01030 
01031                     // Assert that the text selected, is what was planned
01032                     {
01033                         var selectedText = TextEditorSelectionHelper.GetSelectedText(
01034                             primaryCursorModifier,
01035                             modelModifier);
01036 
01037                         Assert.Equal("P", selectedText);
01038                     }
01039 
01040                     return;
01041                 });
01042 
01043             await TextEditorCommandDefaultFacts.IndentLess.CommandFunc.Invoke(textEditorCommandArgs);
01044 
01045             var outModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
01046             Assert.NotNull(outModel);
01047 
01048             var rowText = new string(
01049                 outModel!.GetLineRichCharacterRange(1, 1).Single().Select(x => x.Value).ToArray());
01050             
01051             Assert.Equal("7 Pillows\n", rowText);
01052         }
01053 
01054         // Invoke IndexLess on a single line, of which the line has 4 space characters at the start.
01055         // Only select a single character.
01056         {
01057             InitializeTextEditorCommandDefaultFactsTests(
01058                 out var textEditorService, out var inModel, out var inViewModel,
01059                 out var textEditorCommandArgs, out var serviceProvider);
01060 
01061             textEditorService.PostUnique(
01062                 nameof(TextEditorCommandDefaultFactsTests),
