01196     [Fact]
01197     public async Task ClearTextSelection()
01198     {
01199         // No already existing selection
01200         {
01201             InitializeTextEditorCommandDefaultFactsTests(
01202                 out var textEditorService, out var inModel, out var inViewModel,
01203                 out var textEditorCommandArgs, out var serviceProvider);
01204 
01205             var refViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01206             Assert.NotNull(refViewModel);
01207             Assert.Null(refViewModel!.PrimaryCursor.Selection.AnchorPositionIndex);
01208             Assert.False(TextEditorSelectionHelper.HasSelectedText(refViewModel!.PrimaryCursor.Selection));
01209 
01210             await TextEditorCommandDefaultFacts.ClearTextSelection.CommandFunc.Invoke(textEditorCommandArgs);
01211 
01212             var outViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01213             Assert.NotNull(outViewModel);
01214             Assert.Null(outViewModel!.PrimaryCursor.Selection.AnchorPositionIndex);
01215             Assert.False(TextEditorSelectionHelper.HasSelectedText(outViewModel!.PrimaryCursor.Selection));
01216         }
01217 
01218         // With an already existing selection
01219         {
01220             InitializeTextEditorCommandDefaultFactsTests(
01221                 out var textEditorService, out var inModel, out var inViewModel,
01222                 out var textEditorCommandArgs, out var serviceProvider);
01223 
01224             textEditorService.PostUnique(
01225                 nameof(TextEditorCommandDefaultFactsTests),
01226                 editContext =>
01227                 {
01228                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
01229                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
01230                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
01231                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
01232 
01233                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
01234                         return Task.CompletedTask;
01235 
01236                     // Select the "Hello" text on the first row
01237                     {
01238                         primaryCursorModifier.SelectionAnchorPositionIndex = 0;
01239                         primaryCursorModifier.SelectionEndingPositionIndex = 5;
01240                     }
01241 
01242                     // SetColumnIndexAndPreferred is unnecessary, but the user is likely
01243                     // to have their ColumnIndex == SelectionEndingPositionIndex
01244                     primaryCursorModifier.SetColumnIndexAndPreferred(
01245                         primaryCursorModifier.SelectionEndingPositionIndex);
01246 
01247                     return Task.CompletedTask;
01248                 });
01249 
01250             var refViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01251             Assert.NotNull(refViewModel);
01252             Assert.NotNull(refViewModel!.PrimaryCursor.Selection.AnchorPositionIndex);
01253             Assert.True(TextEditorSelectionHelper.HasSelectedText(refViewModel!.PrimaryCursor.Selection));
01254 
01255             await TextEditorCommandDefaultFacts.ClearTextSelection.CommandFunc.Invoke(textEditorCommandArgs);
01256 
01257             var outViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01258             Assert.NotNull(outViewModel);
01259             Assert.Null(outViewModel!.PrimaryCursor.Selection.AnchorPositionIndex);
01260             Assert.False(TextEditorSelectionHelper.HasSelectedText(outViewModel!.PrimaryCursor.Selection));
01261         }
