01477     [Fact]
01478     public async Task GoToMatchingCharacter()
01479     {
01480         // No shift key
01481         {
01482             InitializeTextEditorCommandDefaultFactsTests(
01483                 out var textEditorService, out var inModel, out var inViewModel,
01484                 out var textEditorCommandArgs, out var serviceProvider);
01485 
01486             var newContent = @"namespace BlazorCrudApp.Persons;
01487 
01488 public class Person
01489 {
01490     public Person()
01491     {
01492     }
01493 }".ReplaceLineEndings("\n");
01494 
01495             var newRowIndex = 3;
01496             var newColumnIndex = 0;
01497 
01498             textEditorService.PostUnique(
01499                 nameof(TextEditorCommandDefaultFactsTests),
01500                 editContext =>
01501                 {
01502                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
01503                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
01504                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
01505                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
01506 
01507                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
01508                         return Task.CompletedTask;
01509 
01510                     modelModifier.SetContent(newContent);
01511 
01512                     // Move the cursor to the first curly brace.
01513                     // In otherwords, the curly brace which opens the 'Person' class.
01514                     {
01515                         primaryCursorModifier.LineIndex = newRowIndex;
01516                         primaryCursorModifier.SetColumnIndexAndPreferred(newColumnIndex);
01517                     }
01518 
01519                     return Task.CompletedTask;
01520                 });
01521 
01522             var refModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
01523             Assert.NotNull(refModel);
01524             Assert.Equal(newContent, refModel!.GetAllText());
01525 
01526             var refViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01527             Assert.NotNull(refViewModel);
01528             Assert.Equal(newRowIndex, refViewModel!.PrimaryCursor.LineIndex);
01529             Assert.Equal(newColumnIndex, refViewModel!.PrimaryCursor.ColumnIndex);
01530             Assert.Equal(newColumnIndex, refViewModel!.PrimaryCursor.PreferredColumnIndex);
01531 
01532             await TextEditorCommandDefaultFacts.GoToMatchingCharacterFactory(false)
01533                 .CommandFunc
01534                 .Invoke(textEditorCommandArgs);
01535 
01536             // The content should NOT have been modified. So Assert that it stayed the same.
01537             {
01538                 var outModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
01539                 Assert.NotNull(outModel);
01540                 Assert.Equal(newContent, outModel!.GetAllText());
01541             }
01542 
01543             var outViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01544             Assert.NotNull(outViewModel);
01545             Assert.Equal(7, outViewModel!.PrimaryCursor.LineIndex);
01546             Assert.Equal(0, outViewModel!.PrimaryCursor.ColumnIndex);
01547             Assert.Equal(0, outViewModel!.PrimaryCursor.ColumnIndex);
01548         }
01549 
01550         // With shift key
01551         {
01552             InitializeTextEditorCommandDefaultFactsTests(
01553                 out var textEditorService, out var inModel, out var inViewModel,
01554                 out var textEditorCommandArgs, out var serviceProvider);
01555 
01556             var newContent = @"namespace BlazorCrudApp.Persons;
01557 
01558 public class Person
01559 {
01560     public Person()
01561     {
01562     }
01563 }".ReplaceLineEndings("\n");
01564 
01565             var newRowIndex = 3;
01566             var newColumnIndex = 0;
01567 
01568             textEditorService.PostUnique(
01569                 nameof(TextEditorCommandDefaultFactsTests),
01570                 editContext =>
01571                 {
01572                     var modelModifier = editContext.GetModelModifier(inModel.ResourceUri);
01573                     var viewModelModifier = editContext.GetViewModelModifier(inViewModel.ViewModelKey);
01574                     var cursorModifierBag = editContext.GetCursorModifierBag(viewModelModifier?.ViewModel);
01575                     var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
01576 
01577                     if (modelModifier is null || viewModelModifier is null || cursorModifierBag is null || primaryCursorModifier is null)
01578                         return Task.CompletedTask;
01579 
01580                     modelModifier.SetContent(newContent);
01581 
01582                     // Move the cursor to the first curly brace.
01583                     // In otherwords, the curly brace which opens the 'Person' class.
01584                     {
01585                         primaryCursorModifier.LineIndex = newRowIndex;
01586                         primaryCursorModifier.SetColumnIndexAndPreferred(newColumnIndex);
01587                     }
01588 
01589                     return Task.CompletedTask;
01590                 });
01591 
01592             var refModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
01593             Assert.NotNull(refModel);
01594             Assert.Equal(newContent, refModel!.GetAllText());
01595 
01596             var refViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01597             Assert.NotNull(refViewModel);
01598             Assert.Equal(newRowIndex, refViewModel!.PrimaryCursor.LineIndex);
01599             Assert.Equal(newColumnIndex, refViewModel!.PrimaryCursor.ColumnIndex);
01600             Assert.Equal(newColumnIndex, refViewModel!.PrimaryCursor.PreferredColumnIndex);
01601 
01602             await TextEditorCommandDefaultFacts.GoToMatchingCharacterFactory(true)
01603                 .CommandFunc
01604                 .Invoke(textEditorCommandArgs);
01605 
01606             // The content should NOT have been modified. So Assert that it stayed the same.
01607             {
01608                 var outModel = textEditorService.ModelApi.GetOrDefault(inModel.ResourceUri);
01609                 Assert.NotNull(outModel);
01610                 Assert.Equal(newContent, outModel!.GetAllText());
01611             }
01612 
01613             var outViewModel = textEditorService.ViewModelApi.GetOrDefault(inViewModel.ViewModelKey);
01614             Assert.NotNull(outViewModel);
01615             Assert.Equal(7, outViewModel!.PrimaryCursor.LineIndex);
01616             Assert.Equal(0, outViewModel!.PrimaryCursor.ColumnIndex);
01617             Assert.Equal(0, outViewModel!.PrimaryCursor.ColumnIndex);
01618             Assert.Equal(54, outViewModel!.PrimaryCursor.Selection.AnchorPositionIndex);
01619             Assert.Equal(88, outViewModel!.PrimaryCursor.Selection.EndingPositionIndex);
01620         }
01621     }
