00044     public static async Task CopyAsync(
00045         ITextEditorEditContext editContext,
00046         TextEditorModelModifier modelModifier,
00047         TextEditorViewModelModifier viewModelModifier,
00048         CursorModifierBagTextEditor cursorModifierBag,
00049         IClipboardService clipboardService)
00050     {
00051         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00052         
00053         var selectedText = TextEditorSelectionHelper.GetSelectedText(primaryCursorModifier, modelModifier);
00054         selectedText ??= modelModifier.GetLineTextRange(primaryCursorModifier.LineIndex, 1);
00055 
00056         await clipboardService.SetClipboard(selectedText).ConfigureAwait(false);
00057         await viewModelModifier.ViewModel.FocusAsync().ConfigureAwait(false);
00058     }
