00060     public static async Task CutAsync(
00061         ITextEditorEditContext editContext,
00062         TextEditorModelModifier modelModifier,
00063         TextEditorViewModelModifier viewModelModifier,
00064         CursorModifierBagTextEditor cursorModifierBag,
00065         IClipboardService clipboardService)
00066     {
00067         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00068         
00069         if (!TextEditorSelectionHelper.HasSelectedText(primaryCursorModifier))
00070         {
00071             var positionIndex = modelModifier.GetPositionIndex(primaryCursorModifier);
00072             var lineInformation = modelModifier.GetLineInformationFromPositionIndex(positionIndex);
00073 
00074             primaryCursorModifier.SelectionAnchorPositionIndex = lineInformation.StartPositionIndexInclusive;
00075             primaryCursorModifier.SelectionEndingPositionIndex = lineInformation.EndPositionIndexExclusive;
00076         }
00077 
00078         var selectedText = TextEditorSelectionHelper.GetSelectedText(primaryCursorModifier, modelModifier) ?? string.Empty;
00079         await clipboardService.SetClipboard(selectedText).ConfigureAwait(false);
00080 
00081         await viewModelModifier.ViewModel.FocusAsync().ConfigureAwait(false);
00082 
00083         modelModifier.HandleKeyboardEvent(
00084             new KeymapArgs { Key = KeyboardKeyFacts.MetaKeys.DELETE },
00085             cursorModifierBag,
00086             CancellationToken.None);
00087     }
