00102     public static void TriggerSave(
00103         ITextEditorEditContext editContext,
00104         TextEditorModelModifier modelModifier,
00105         TextEditorViewModelModifier viewModelModifier,
00106         CursorModifierBagTextEditor cursorModifierBag,
00107         TextEditorCommandArgs commandArgs)
00108     {
00109         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00110         
00111         if (viewModelModifier.ViewModel.OnSaveRequested is null)
00112         {
00113             NotificationHelper.DispatchError(
00114                 nameof(TriggerSave),
00115                 $"{nameof(TriggerSave)} was null",
00116                 commandArgs.ServiceProvider.GetRequiredService<ICommonComponentRenderers>(),
00117                 commandArgs.ServiceProvider.GetRequiredService<IDispatcher>(),
00118                 TimeSpan.FromSeconds(7));
00119         }
00120         else
00121         {
00122             viewModelModifier.ViewModel.OnSaveRequested.Invoke(modelModifier);
00123             modelModifier.SetIsDirtyFalse();
00124         }
00125     }
