00257     public static void Duplicate(
00258         ITextEditorEditContext editContext,
00259         TextEditorModelModifier modelModifier,
00260         TextEditorViewModelModifier viewModelModifier,
00261         CursorModifierBagTextEditor cursorModifierBag,
00262         TextEditorCommandArgs commandArgs)
00263     {
00264         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00265     
00266         var selectedText = TextEditorSelectionHelper.GetSelectedText(primaryCursorModifier, modelModifier);
00267 
00268         TextEditorCursor cursorForInsertion;
00269         if (selectedText is null)
00270         {
00271             // Select line
00272             selectedText = modelModifier.GetLineTextRange(primaryCursorModifier.LineIndex, 1);
00273 
00274             cursorForInsertion = new TextEditorCursor(
00275                 primaryCursorModifier.LineIndex,
00276                 0,
00277                 primaryCursorModifier.IsPrimaryCursor);
00278         }
00279         else
00280         {
00281             // Clone the TextEditorCursor to remove the TextEditorSelection otherwise the
00282             // selected text to duplicate would be overwritten by itself and do nothing
00283             cursorForInsertion = primaryCursorModifier.ToCursor() with
00284             {
00285                 Selection = TextEditorSelection.Empty
00286             };
00287         }
00288 
00289         modelModifier.Insert(
00290             selectedText,
00291             new CursorModifierBagTextEditor(Key<TextEditorViewModel>.Empty, new List<TextEditorCursorModifier>() { new(cursorForInsertion) }),
00292             cancellationToken: CancellationToken.None);
00293     }
