00295     public static void IndentMore(
00296         ITextEditorEditContext editContext,
00297         TextEditorModelModifier modelModifier,
00298         TextEditorViewModelModifier viewModelModifier,
00299         CursorModifierBagTextEditor cursorModifierBag,
00300         TextEditorCommandArgs commandArgs)
00301     {
00302         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00303     
00304         if (!TextEditorSelectionHelper.HasSelectedText(primaryCursorModifier))
00305             return;
00306 
00307         var selectionBoundsInPositionIndexUnits = TextEditorSelectionHelper.GetSelectionBounds(primaryCursorModifier);
00308 
00309         var selectionBoundsInRowIndexUnits = TextEditorSelectionHelper.ConvertSelectionOfPositionIndexUnitsToRowIndexUnits(
00310             modelModifier,
00311             selectionBoundsInPositionIndexUnits);
00312 
00313         for (var i = selectionBoundsInRowIndexUnits.lowerRowIndexInclusive;
00314              i < selectionBoundsInRowIndexUnits.upperRowIndexExclusive;
00315              i++)
00316         {
00317             var insertionCursor = new TextEditorCursor(i, 0, true);
00318 
00319             var insertionCursorModifierBag = new CursorModifierBagTextEditor(
00320                 Key<TextEditorViewModel>.Empty,
00321                 new List<TextEditorCursorModifier> { new TextEditorCursorModifier(insertionCursor) });
00322 
00323             modelModifier.Insert(
00324                 KeyboardKeyFacts.WhitespaceCharacters.TAB.ToString(),
00325                 insertionCursorModifierBag,
00326                 cancellationToken: CancellationToken.None);
00327         }
00328 
00329         var lowerBoundPositionIndexChange = 1;
00330 
00331         var upperBoundPositionIndexChange = selectionBoundsInRowIndexUnits.upperRowIndexExclusive -
00332             selectionBoundsInRowIndexUnits.lowerRowIndexInclusive;
00333 
00334         if (primaryCursorModifier.SelectionAnchorPositionIndex < primaryCursorModifier.SelectionEndingPositionIndex)
00335         {
00336             primaryCursorModifier.SelectionAnchorPositionIndex += lowerBoundPositionIndexChange;
00337             primaryCursorModifier.SelectionEndingPositionIndex += upperBoundPositionIndexChange;
00338         }
00339         else
00340         {
00341             primaryCursorModifier.SelectionAnchorPositionIndex += upperBoundPositionIndexChange;
00342             primaryCursorModifier.SelectionEndingPositionIndex += lowerBoundPositionIndexChange;
00343         }
00344 
00345         primaryCursorModifier.SetColumnIndexAndPreferred(1 + primaryCursorModifier.ColumnIndex);
00346     }
