00348     public static void IndentLess(
00349         ITextEditorEditContext editContext,
00350         TextEditorModelModifier modelModifier,
00351         TextEditorViewModelModifier viewModelModifier,
00352         CursorModifierBagTextEditor cursorModifierBag,
00353         TextEditorCommandArgs commandArgs)
00354     {
00355         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00356     
00357         var selectionBoundsInPositionIndexUnits = TextEditorSelectionHelper.GetSelectionBounds(primaryCursorModifier);
00358 
00359         var selectionBoundsInRowIndexUnits = TextEditorSelectionHelper.ConvertSelectionOfPositionIndexUnitsToRowIndexUnits(
00360             modelModifier,
00361             selectionBoundsInPositionIndexUnits);
00362 
00363         bool isFirstLoop = true;
00364 
00365         for (var i = selectionBoundsInRowIndexUnits.lowerRowIndexInclusive;
00366              i < selectionBoundsInRowIndexUnits.upperRowIndexExclusive;
00367              i++)
00368         {
00369             var rowPositionIndex = modelModifier.GetPositionIndex(i, 0);
00370             var characterReadCount = TextEditorModel.TAB_WIDTH;
00371             var lengthOfRow = modelModifier.GetLineLength(i);
00372 
00373             characterReadCount = Math.Min(lengthOfRow, characterReadCount);
00374 
00375             var readResult = modelModifier.GetString(rowPositionIndex, characterReadCount);
00376             var removeCharacterCount = 0;
00377 
00378             if (readResult.StartsWith(KeyboardKeyFacts.WhitespaceCharacters.TAB))
00379             {
00380                 removeCharacterCount = 1;
00381 
00382                 var cursorForDeletion = new TextEditorCursor(i, 0, true);
00383 
00384                 modelModifier.DeleteByRange(
00385                     removeCharacterCount, // Delete a single "Tab" character
00386                     new CursorModifierBagTextEditor(Key<TextEditorViewModel>.Empty, new List<TextEditorCursorModifier> { new(cursorForDeletion) }),
00387                     CancellationToken.None);
00388             }
00389             else if (readResult.StartsWith(KeyboardKeyFacts.WhitespaceCharacters.SPACE))
00390             {
00391                 var cursorForDeletion = new TextEditorCursor(i, 0, true);
00392                 var contiguousSpaceCount = 0;
00393 
00394                 foreach (var character in readResult)
00395                 {
00396                     if (character == KeyboardKeyFacts.WhitespaceCharacters.SPACE)
00397                         contiguousSpaceCount++;
00398                 }
00399 
00400                 removeCharacterCount = contiguousSpaceCount;
00401 
00402                 modelModifier.DeleteByRange(
00403                     removeCharacterCount,
00404                     new CursorModifierBagTextEditor(Key<TextEditorViewModel>.Empty, new List<TextEditorCursorModifier> { new(cursorForDeletion) }),
00405                     CancellationToken.None);
00406             }
00407 
00408             // Modify the lower bound of user's text selection
00409             if (isFirstLoop)
00410             {
00411                 isFirstLoop = false;
00412 
00413                 if (primaryCursorModifier.SelectionAnchorPositionIndex < primaryCursorModifier.SelectionEndingPositionIndex)
00414                     primaryCursorModifier.SelectionAnchorPositionIndex -= removeCharacterCount;
00415                 else
00416                     primaryCursorModifier.SelectionEndingPositionIndex -= removeCharacterCount;
00417             }
00418 
00419             // Modify the upper bound of user's text selection
00420             if (primaryCursorModifier.SelectionAnchorPositionIndex < primaryCursorModifier.SelectionEndingPositionIndex)
00421                 primaryCursorModifier.SelectionEndingPositionIndex -= removeCharacterCount;
00422             else
00423                 primaryCursorModifier.SelectionAnchorPositionIndex -= removeCharacterCount;
00424 
00425             // Modify the column index of user's cursor
00426             if (i == primaryCursorModifier.LineIndex)
00427             {
00428                 var nextColumnIndex = primaryCursorModifier.ColumnIndex - removeCharacterCount;
00429 
00430                 primaryCursorModifier.LineIndex = primaryCursorModifier.LineIndex;
00431                 primaryCursorModifier.ColumnIndex = Math.Max(0, nextColumnIndex);
00432             }
00433         }
00434     }
