00447     public static void NewLineBelow(
00448         ITextEditorEditContext editContext,
00449         TextEditorModelModifier modelModifier,
00450         TextEditorViewModelModifier viewModelModifier,
00451         CursorModifierBagTextEditor cursorModifierBag,
00452         TextEditorCommandArgs commandArgs)
00453     {
00454         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00455         primaryCursorModifier.SelectionAnchorPositionIndex = null;
00456 
00457         var lengthOfRow = modelModifier.GetLineLength(primaryCursorModifier.LineIndex);
00458 
00459         primaryCursorModifier.LineIndex = primaryCursorModifier.LineIndex;
00460         primaryCursorModifier.ColumnIndex = lengthOfRow;
00461         
00462         // NOTE: keep the value to insert as '\n' because this will be changed to the user's
00463         //       preferred line ending upon insertion.
00464         var valueToInsert = "\n";
00465         
00466         // GOAL: Match indentation on newline keystroke (2024-07-07)
00467         {
00468             var line = modelModifier.GetLineInformation(primaryCursorModifier.LineIndex);
00469 
00470             var cursorPositionIndex = line.StartPositionIndexInclusive + primaryCursorModifier.ColumnIndex;
00471             var indentationPositionIndex = line.StartPositionIndexInclusive;
00472 
00473             var indentationBuilder = new StringBuilder();
00474 
00475             while (indentationPositionIndex < cursorPositionIndex)
00476             {
00477                 var possibleIndentationChar = modelModifier.RichCharacterList[indentationPositionIndex++].Value;
00478 
00479                 if (possibleIndentationChar == '\t' || possibleIndentationChar == ' ')
00480                     indentationBuilder.Append(possibleIndentationChar);
00481                 else
00482                     break;
00483             }
00484 
00485             valueToInsert += indentationBuilder.ToString();
00486         }
00487 
00488         modelModifier.Insert(valueToInsert, cursorModifierBag, cancellationToken: CancellationToken.None);
00489     }
