00491     public static void NewLineAbove(
00492         ITextEditorEditContext editContext,
00493         TextEditorModelModifier modelModifier,
00494         TextEditorViewModelModifier viewModelModifier,
00495         CursorModifierBagTextEditor cursorModifierBag,
00496         TextEditorCommandArgs commandArgs)
00497     {
00498         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00499         primaryCursorModifier.SelectionAnchorPositionIndex = null;
00500             
00501         var originalColumnIndex = primaryCursorModifier.ColumnIndex;
00502 
00503         primaryCursorModifier.LineIndex = primaryCursorModifier.LineIndex;
00504         primaryCursorModifier.ColumnIndex = 0;
00505         
00506         // NOTE: keep the value to insert as '\n' because this will be changed to the user's
00507         //       preferred line ending upon insertion.
00508         var valueToInsert = "\n";
00509         
00510         var indentationLength = 0;
00511         
00512         // GOAL: Match indentation on newline keystroke (2024-07-07)
00513         {
00514             var line = modelModifier.GetLineInformation(primaryCursorModifier.LineIndex);
00515 
00516             var cursorPositionIndex = line.StartPositionIndexInclusive + originalColumnIndex;
00517             var indentationPositionIndex = line.StartPositionIndexInclusive;
00518 
00519             var indentationBuilder = new StringBuilder();
00520 
00521             while (indentationPositionIndex < cursorPositionIndex)
00522             {
00523                 var possibleIndentationChar = modelModifier.RichCharacterList[indentationPositionIndex++].Value;
00524 
00525                 if (possibleIndentationChar == '\t' || possibleIndentationChar == ' ')
00526                     indentationBuilder.Append(possibleIndentationChar);
00527                 else
00528                     break;
00529             }
00530 
00531             valueToInsert = indentationBuilder.ToString() + valueToInsert;
00532             indentationLength = indentationBuilder.Length;
00533         }
00534 
00535         modelModifier.Insert(valueToInsert, cursorModifierBag, cancellationToken: CancellationToken.None);
00536 
00537         if (primaryCursorModifier.LineIndex > 1)
00538         {
00539             primaryCursorModifier.LineIndex--;
00540             primaryCursorModifier.ColumnIndex = indentationLength;
00541         }
00542     }
