00544     public static void MoveLineDown(
00545         ITextEditorEditContext editContext,
00546         TextEditorModelModifier modelModifier,
00547         TextEditorViewModelModifier viewModelModifier,
00548         CursorModifierBagTextEditor cursorModifierBag,
00549         TextEditorCommandArgs commandArgs)
00550     {
00551         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00552         var lineIndexOriginal = primaryCursorModifier.LineIndex;
00553         var columnIndexOriginal = primaryCursorModifier.ColumnIndex;
00554 
00555         var nextLineIndex = lineIndexOriginal + 1;
00556         var nextLineInformation = modelModifier.GetLineInformation(nextLineIndex);
00557 
00558         // Insert
00559         {
00560             var currentLineContent = modelModifier.GetLineTextRange(lineIndexOriginal, 1);
00561         
00562             primaryCursorModifier.LineIndex = nextLineIndex + 1;
00563             primaryCursorModifier.ColumnIndex = 0;
00564             
00565             var innerCursorModifierBag = new CursorModifierBagTextEditor(
00566                 Key<TextEditorViewModel>.Empty,
00567                 new List<TextEditorCursorModifier> { primaryCursorModifier });
00568 
00569             modelModifier.Insert(
00570                 value: currentLineContent,
00571                 cursorModifierBag: innerCursorModifierBag,
00572                 useLineEndKindPreference: false);
00573         }
00574 
00575         // Delete
00576         {
00577             primaryCursorModifier.LineIndex = lineIndexOriginal;
00578             primaryCursorModifier.ColumnIndex = 0;
00579             
00580             var currentLineInformation = modelModifier.GetLineInformation(primaryCursorModifier.LineIndex);
00581             var columnCount = currentLineInformation.EndPositionIndexExclusive -
00582                 currentLineInformation.StartPositionIndexInclusive;
00583 
00584             var innerCursorModifierBag = new CursorModifierBagTextEditor(
00585                 Key<TextEditorViewModel>.Empty,
00586                 new List<TextEditorCursorModifier> { primaryCursorModifier });
00587 
00588             modelModifier.Delete(
00589                 innerCursorModifierBag,
00590                 columnCount,
00591                 false,
00592                 TextEditorModelModifier.DeleteKind.Delete);
00593         }
00594         
00595         primaryCursorModifier.LineIndex = lineIndexOriginal + 1;
00596         primaryCursorModifier.ColumnIndex = 0;
00597     }
