00599     public static void MoveLineUp(
00600         ITextEditorEditContext editContext,
00601         TextEditorModelModifier modelModifier,
00602         TextEditorViewModelModifier viewModelModifier,
00603         CursorModifierBagTextEditor cursorModifierBag,
00604         TextEditorCommandArgs commandArgs)
00605     {
00606         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00607         var lineIndexOriginal = primaryCursorModifier.LineIndex;
00608         var columnIndexOriginal = primaryCursorModifier.ColumnIndex;
00609             
00610         var previousLineIndex = lineIndexOriginal - 1;
00611         var previousLineInformation = modelModifier.GetLineInformation(previousLineIndex);
00612 
00613         // Insert
00614         {
00615             var currentLineContent = modelModifier.GetLineTextRange(lineIndexOriginal, 1);
00616         
00617             primaryCursorModifier.LineIndex = previousLineIndex;
00618             primaryCursorModifier.ColumnIndex = 0;
00619 
00620             var innerCursorModifierBag = new CursorModifierBagTextEditor(
00621                 Key<TextEditorViewModel>.Empty,
00622                 new List<TextEditorCursorModifier> { primaryCursorModifier });
00623 
00624             modelModifier.Insert(
00625                 value: currentLineContent,
00626                 cursorModifierBag: innerCursorModifierBag,
00627                 useLineEndKindPreference: false);
00628         }
00629 
00630         // Delete
00631         {
00632             // Add 1 because a line was inserted
00633             primaryCursorModifier.LineIndex = lineIndexOriginal + 1;
00634             primaryCursorModifier.ColumnIndex = 0;
00635             
00636             var currentLineInformation = modelModifier.GetLineInformation(primaryCursorModifier.LineIndex);
00637             var columnCount = currentLineInformation.EndPositionIndexExclusive -
00638                 currentLineInformation.StartPositionIndexInclusive;
00639 
00640             var innerCursorModifierBag = new CursorModifierBagTextEditor(
00641                 Key<TextEditorViewModel>.Empty,
00642                 new List<TextEditorCursorModifier> { primaryCursorModifier });
00643 
00644             modelModifier.Delete(
00645                 innerCursorModifierBag,
00646                 columnCount,
00647                 false,
00648                 TextEditorModelModifier.DeleteKind.Delete);
00649         }
00650         
00651         primaryCursorModifier.LineIndex = lineIndexOriginal - 1;
00652         primaryCursorModifier.ColumnIndex = 0;
00653     }
00654 
00655     public static void GoToMatchingCharacter(
00656         ITextEditorEditContext editContext,
00657         TextEditorModelModifier modelModifier,
00658         TextEditorViewModelModifier viewModelModifier,
00659         CursorModifierBagTextEditor cursorModifierBag,
00660         TextEditorCommandArgs commandArgs)
00661     {
00662         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
00663         var cursorPositionIndex = modelModifier.GetPositionIndex(primaryCursorModifier);
00664 
00665         if (commandArgs.ShouldSelectText)
00666         {
00667             if (!TextEditorSelectionHelper.HasSelectedText(primaryCursorModifier))
00668                 primaryCursorModifier.SelectionAnchorPositionIndex = cursorPositionIndex;
00669         }
00670         else
00671         {
00672             primaryCursorModifier.SelectionAnchorPositionIndex = null;
00673         }
00674 
00675         var previousCharacter = modelModifier.GetCharacter(cursorPositionIndex - 1);
00676         var currentCharacter = modelModifier.GetCharacter(cursorPositionIndex);
00677 
00678         char? characterToMatch = null;
00679         char? match = null;
00680 
00681         var fallbackToPreviousCharacter = false;
00682 
00683         if (CharacterKindHelper.CharToCharacterKind(currentCharacter) == CharacterKind.Punctuation)
00684         {
00685             // Prefer current character
00686             match = KeyboardKeyFacts.MatchPunctuationCharacter(currentCharacter);
00687 
00688             if (match is not null)
00689                 characterToMatch = currentCharacter;
00690         }
00691 
00692         if (characterToMatch is null && CharacterKindHelper.CharToCharacterKind(previousCharacter) == CharacterKind.Punctuation)
00693         {
00694             // Fallback to the previous current character
00695             match = KeyboardKeyFacts.MatchPunctuationCharacter(previousCharacter);
00696 
00697             if (match is not null)
00698             {
00699                 characterToMatch = previousCharacter;
00700                 fallbackToPreviousCharacter = true;
00701             }
00702         }
00703 
00704         if (characterToMatch is null || match is null)
00705             return;
00706 
00707         var directionToFindMatchingPunctuationCharacter = KeyboardKeyFacts.DirectionToFindMatchingPunctuationCharacter(
00708             characterToMatch.Value);
00709 
00710         if (directionToFindMatchingPunctuationCharacter is null)
00711             return;
00712 
00713         var unmatchedCharacters = fallbackToPreviousCharacter && -1 == directionToFindMatchingPunctuationCharacter
00714             ? 0
00715             : 1;
00716 
00717         while (true)
00718         {
00719             KeymapArgs keymapArgs;
00720 
00721             if (directionToFindMatchingPunctuationCharacter == -1)
00722             {
00723                 keymapArgs = new KeymapArgs
00724                 {
00725                     Key = KeyboardKeyFacts.MovementKeys.ARROW_LEFT,
00726                     ShiftKey = commandArgs.ShouldSelectText,
00727                 };
00728             }
00729             else
00730             {
00731                 keymapArgs = new KeymapArgs
00732                 {
00733                     Key = KeyboardKeyFacts.MovementKeys.ARROW_RIGHT,
00734                     ShiftKey = commandArgs.ShouldSelectText,
00735                 };
00736             }
00737 
00738             editContext.TextEditorService.ViewModelApi.MoveCursorUnsafe(
00739                 keymapArgs,
00740                 editContext,
00741                 modelModifier,
00742                 viewModelModifier,
00743                 cursorModifierBag,
00744                 primaryCursorModifier);
00745 
00746             var positionIndex = modelModifier.GetPositionIndex(primaryCursorModifier);
00747             var characterAt = modelModifier.GetCharacter(positionIndex);
00748 
00749             if (characterAt == match)
00750                 unmatchedCharacters--;
00751             else if (characterAt == characterToMatch)
00752                 unmatchedCharacters++;
00753 
00754             if (unmatchedCharacters == 0)
00755                 break;
00756 
00757             if (positionIndex <= 0 || positionIndex >= modelModifier.CharCount)
00758                 break;
00759         }
00760 
00761         if (commandArgs.ShouldSelectText)
00762             primaryCursorModifier.SelectionEndingPositionIndex = modelModifier.GetPositionIndex(primaryCursorModifier);
00763     }
