00765     public static async Task RelatedFilesQuickPick(
00766         ITextEditorEditContext editContext,
00767         TextEditorModelModifier modelModifier,
00768         TextEditorViewModelModifier viewModelModifier,
00769         CursorModifierBagTextEditor cursorModifierBag,
00770         TextEditorCommandArgs commandArgs)
00771     {
00772         var jsRuntime = commandArgs.ServiceProvider.GetRequiredService<IJSRuntime>();
00773         var jsRuntimeCommonApi = jsRuntime.GetLuthetusCommonApi();
00774                
00775         var cursorDimensions = await jsRuntimeCommonApi
00776             .MeasureElementById(viewModelModifier.ViewModel.PrimaryCursorContentId)
00777             .ConfigureAwait(false);
00778 
00779         var environmentProvider = commandArgs.ServiceProvider.GetRequiredService<IEnvironmentProvider>();
00780         
00781         var resourceAbsolutePath = environmentProvider.AbsolutePathFactory(modelModifier.ResourceUri.Value, false);
00782         var parentDirectoryAbsolutePath = environmentProvider.AbsolutePathFactory(resourceAbsolutePath.ParentDirectory.Value, true);
00783     
00784         var fileSystemProvider = commandArgs.ServiceProvider.GetRequiredService<IFileSystemProvider>();
00785         
