00787         
00788         try
00789         {
00790             siblingFileStringList = await fileSystemProvider.Directory
00791                 .GetFilesAsync(parentDirectoryAbsolutePath.Value)
00792                 .ConfigureAwait(false);
00793         }
00794         catch (Exception e)
00795         {
00796             Console.WriteLine(e);
00797         }
00798         
00799         var menuOptionList = new List<MenuOptionRecord>();
00800         
