00822                 initialActiveMenuOptionRecordIndex = i;
00823         }
00824         
00825         MenuRecord menu;
00826         
00827         if (menuOptionList.Count == 0)
00828             menu = MenuRecord.Empty;
00829         else
00830             menu = new MenuRecord(menuOptionList.ToImmutableArray());
00831         
00832         var dropdownRecord = new DropdownRecord(
00833             Key<DropdownRecord>.NewKey(),
00834             cursorDimensions.LeftInPixels,
00835             cursorDimensions.TopInPixels + cursorDimensions.HeightInPixels,
00836             typeof(MenuDisplay),
00837             new Dictionary<string, object?>
00838             {
00839                 {
00840                     nameof(MenuDisplay.MenuRecord),
00841                     menu
00842                 },
00843                 {
00844                     nameof(MenuDisplay.InitialActiveMenuOptionRecordIndex),
00845                     initialActiveMenuOptionRecordIndex
00846                 }
00847             },
00848             // TODO: this callback when the dropdown closes is suspect.
00849             //       The editContext is supposed to live the lifespan of the
00850             //       Post. But what if the Post finishes before the dropdown is closed?
00851             () => 
00852             {
00853                 // TODO: Even if this '.single or default' to get the main group works it is bad and I am ashamed...
00854                 //       ...I'm too tired at the moment, need to make this sensible.
00855                 //     The key is in the IDE project yet its circular reference if I do so, gotta
00856                 //       make groups more sensible I'm not sure what to say here I'm super tired and brain checked out.
00857                 //       |
00858                 //       I ran this and it didn't work. Its for the best that it doesn't.
00859                 //     maybe when I wake up tomorrow I'll realize what im doing here.
00860                 var mainEditorGroup = commandArgs.TextEditorService.GroupStateWrap.Value.GroupList.SingleOrDefault();
00861                 
00862                 if (mainEditorGroup is not null &&
00863                     mainEditorGroup.ActiveViewModelKey != Key<TextEditorViewModel>.Empty)
00864                 {
00865                     var activeViewModel = commandArgs.TextEditorService.ViewModelApi.GetOrDefault(mainEditorGroup.ActiveViewModelKey);
00866 
00867                     if (activeViewModel is not null)
00868                         return activeViewModel.FocusAsync();
00869                 }
00870                 
