01027     public static void GoToDefinition(
01028         ITextEditorEditContext editContext,
01029         TextEditorModelModifier modelModifier,
01030         TextEditorViewModelModifier viewModelModifier,
01031         CursorModifierBagTextEditor cursorModifierBag,
01032         TextEditorCommandArgs commandArgs)
01033     {
01034         if (modelModifier.CompilerService.Binder is null)
01035             return;
01036             
01037         var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
01038 
01039         var positionIndex = modelModifier.GetPositionIndex(primaryCursorModifier);
01040         
01041         var wordTextSpan = modelModifier.GetWordTextSpan(positionIndex);
01042         if (wordTextSpan is null)
01043             return;
01044 
01045         var compilerServiceResource = modelModifier.CompilerService.GetCompilerServiceResourceFor(modelModifier.ResourceUri);
01046         if (compilerServiceResource?.CompilationUnit is null)
01047             return;
01048 
01049         var definitionTextSpan = modelModifier.CompilerService.Binder.GetDefinition(wordTextSpan.Value, compilerServiceResource);
01050         if (definitionTextSpan is null)
01051             return;
01052 
01053         var definitionModel = commandArgs.TextEditorService.ModelApi.GetOrDefault(definitionTextSpan.Value.ResourceUri);
01054         if (definitionModel is null)
01055         {
01056             if (commandArgs.TextEditorService.TextEditorConfig.RegisterModelFunc is not null)
01057             {
01058                 commandArgs.TextEditorService.TextEditorConfig.RegisterModelFunc.Invoke(
01059                     new RegisterModelArgs(definitionTextSpan.Value.ResourceUri, commandArgs.ServiceProvider));
01060 
01061                 var definitionModelModifier = editContext.GetModelModifier(definitionTextSpan.Value.ResourceUri);
01062 
01063                 if (definitionModel is null) // TODO: Should this be null checking instead: 'definitionModelModifier'?
01064                     return;
01065             }
01066             else
01067             {
01068                 return;
01069             }
01070         }
01071 
01072         var definitionViewModels = commandArgs.TextEditorService.ModelApi.GetViewModelsOrEmpty(definitionTextSpan.Value.ResourceUri);
01073 
01074         if (!definitionViewModels.Any())
01075         {
01076             if (commandArgs.TextEditorService.TextEditorConfig.TryRegisterViewModelFunc is not null)
01077             {
01078                 commandArgs.TextEditorService.TextEditorConfig.TryRegisterViewModelFunc.Invoke(new TryRegisterViewModelArgs(
01079                     Key<TextEditorViewModel>.NewKey(),
01080                     definitionTextSpan.Value.ResourceUri,
01081                     new Category("main"),
01082                     true,
01083                     commandArgs.ServiceProvider));
01084 
01085                 definitionViewModels = commandArgs.TextEditorService.ModelApi.GetViewModelsOrEmpty(definitionTextSpan.Value.ResourceUri);
01086 
01087                 if (!definitionViewModels.Any())
01088                     return;
01089             }
01090             else
01091             {
01092                 return;
01093             }
01094         }
01095 
01096         var definitionViewModelKey = definitionViewModels.First().ViewModelKey;
01097         
01098         var definitionViewModelModifier = editContext.GetViewModelModifier(definitionViewModelKey);
01099         var definitionCursorModifierBag = editContext.GetCursorModifierBag(definitionViewModelModifier?.ViewModel);
01100         var definitionPrimaryCursorModifier = editContext.GetPrimaryCursorModifier(definitionCursorModifierBag);
01101 
01102         if (definitionViewModelModifier is null || definitionCursorModifierBag is null || definitionPrimaryCursorModifier is null)
01103             return;
01104 
01105         var rowData = definitionModel.GetLineInformationFromPositionIndex(definitionTextSpan.Value.StartingIndexInclusive);
01106         var columnIndex = definitionTextSpan.Value.StartingIndexInclusive - rowData.StartPositionIndexInclusive;
01107 
01108         definitionPrimaryCursorModifier.SelectionAnchorPositionIndex = null;
01109         definitionPrimaryCursorModifier.LineIndex = rowData.Index;
01110         definitionPrimaryCursorModifier.ColumnIndex = columnIndex;
01111         definitionPrimaryCursorModifier.PreferredColumnIndex = columnIndex;
01112 
01113         if (commandArgs.TextEditorService.TextEditorConfig.TryShowViewModelFunc is not null)
01114         {
01115             commandArgs.TextEditorService.TextEditorConfig.TryShowViewModelFunc.Invoke(new TryShowViewModelArgs(
01116                 definitionViewModelKey,
01117                 Key<TextEditorGroup>.Empty,
01118                 true,
01119                 commandArgs.ServiceProvider));
01120         }
01121     }
