01141             .GetBoundingClientRect(viewModelModifier.ViewModel.PrimaryCursorContentId)
01142             .ConfigureAwait(false);
01143 
01144         elementPositionInPixels = elementPositionInPixels with
01145         {
01146             Top = elementPositionInPixels.Top +
01147                 (.9 * viewModelModifier.ViewModel.CharAndLineMeasurements.LineHeight)
01148         };
01149 
01150         await HandleMouseStoppedMovingEventAsync(
01151                 editContext,
