01166     public static async Task HandleAfterOnKeyDownAsync(
01167         ITextEditorEditContext editContext,
01168         TextEditorModelModifier modelModifier,
01169         TextEditorViewModelModifier viewModelModifier,
01170         CursorModifierBagTextEditor cursorModifierBag,
01171         KeymapArgs keymapArgs,
01172         TextEditorComponentData componentData)
01173     {
01174         // Indexing can be invoked and this method still check for syntax highlighting and such
01175         if (EventUtils.IsAutocompleteIndexerInvoker(keymapArgs))
01176         {
01177             try
01178             {
01179                 var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
01180 
01181                 if (primaryCursorModifier.ColumnIndex > 0)
01182                 {
01183                     // All keyboardEventArgs that return true from "IsAutocompleteIndexerInvoker"
01184                     // are to be 1 character long, as well either specific whitespace or punctuation.
01185                     // Therefore 1 character behind might be a word that can be indexed.
01186                     var word = modelModifier.ReadPreviousWordOrDefault(
01187                         primaryCursorModifier.LineIndex,
01188                         primaryCursorModifier.ColumnIndex);
01189 
01190                     if (word is not null)
01191                     {
01192                         _ = Task.Run(async () =>
01193                         {
01194                             await editContext.TextEditorService.AutocompleteIndexer
01195                                 .IndexWordAsync(word)
01196                                 .ConfigureAwait(false);
01197                         });
01198                     }
01199                 }
01200             }
01201             catch (LuthetusTextEditorException e)
01202             {
01203                 // Eat this exception
01204             }
01205         }
01206 
01207         if (EventUtils.IsAutocompleteMenuInvoker(keymapArgs))
01208         {
01209             ShowAutocompleteMenu(
01210                 editContext,
01211                 modelModifier,
01212                 viewModelModifier,
01213                 cursorModifierBag,
01214                 editContext.GetPrimaryCursorModifier(cursorModifierBag),
01215                 componentData.Dispatcher,
01216                 componentData);
01217         }
01218         else if (EventUtils.IsSyntaxHighlightingInvoker(keymapArgs))
01219         {
01220             componentData.ThrottleApplySyntaxHighlighting(modelModifier);
01221         }
01222     }
01223 
01243     public static async Task HandleAfterOnKeyDownRangeAsync(
01244         ITextEditorEditContext editContext,
01245         TextEditorModelModifier modelModifier,
01246         TextEditorViewModelModifier viewModelModifier,
01247         CursorModifierBagTextEditor cursorModifierBag,
01248         KeymapArgs[] batchKeymapArgsList,
01249         int batchKeymapArgsListLength,
01250         TextEditorComponentData componentData,
01251         ViewModelDisplayOptions viewModelDisplayOptions)
01252     {
01253         if (viewModelDisplayOptions.AfterOnKeyDownRangeAsync is not null)
01254         {
01255             await viewModelDisplayOptions.AfterOnKeyDownRangeAsync.Invoke(
01256                 editContext,
01257                 modelModifier,
01258                 viewModelModifier,
01259                 cursorModifierBag,
01260                 batchKeymapArgsList,
01261                 batchKeymapArgsListLength,
01262                 componentData);
01263             return;
01264         }
01265 
01266         var seenIsAutocompleteIndexerInvoker = false;
01267         var seenIsAutocompleteMenuInvoker = false;
01268         var seenIsSyntaxHighlightingInvoker = false;
01269 
01270         for (int i = 0; i < batchKeymapArgsListLength; i++)
01271         {
01272             var keymapArgs = batchKeymapArgsList[i];
01273 
01274             if (!seenIsAutocompleteIndexerInvoker && EventUtils.IsAutocompleteIndexerInvoker(keymapArgs))
01275                 seenIsAutocompleteIndexerInvoker = true;
01276 
01277             if (!seenIsAutocompleteMenuInvoker && EventUtils.IsAutocompleteMenuInvoker(keymapArgs))
01278                 seenIsAutocompleteMenuInvoker = true;
01279             else if (!seenIsSyntaxHighlightingInvoker && EventUtils.IsSyntaxHighlightingInvoker(keymapArgs))
01280                 seenIsSyntaxHighlightingInvoker = true;
01281         }
01282 
01283         if (seenIsAutocompleteIndexerInvoker)
01284         {
01285             var primaryCursorModifier = editContext.GetPrimaryCursorModifier(cursorModifierBag);
01286             
01287             if (primaryCursorModifier.ColumnIndex > 0)
01288             {
01289                 // All keyboardEventArgs that return true from "IsAutocompleteIndexerInvoker"
01290                 // are to be 1 character long, as well either specific whitespace or punctuation.
01291                 // Therefore 1 character behind might be a word that can be indexed.
01292                 var word = modelModifier.ReadPreviousWordOrDefault(
01293                     primaryCursorModifier.LineIndex,
01294                     primaryCursorModifier.ColumnIndex);
01295 
01296                 if (word is not null)
01297                 {
01298                     _ = Task.Run(async () =>
01299                     {
01300                         await editContext.TextEditorService.AutocompleteIndexer
01301                             .IndexWordAsync(word)
01302                             .ConfigureAwait(false);
01303                     });
01304                 }
01305             }
01306         }
01307 
01308         if (seenIsAutocompleteMenuInvoker)
01309         {
01310             ShowAutocompleteMenu(
01311                 editContext,
01312                 modelModifier,
01313                 viewModelModifier,
01314                 cursorModifierBag,
01315                 editContext.GetPrimaryCursorModifier(cursorModifierBag),
01316                 componentData.Dispatcher,
01317                 componentData);
01318         }
01319 
01320         if (seenIsSyntaxHighlightingInvoker)
01321         {
01322             componentData.ThrottleApplySyntaxHighlighting(modelModifier);
01323         }
01324     }
01325 
01326     public static async Task HandleMouseStoppedMovingEventAsync(
01327         ITextEditorEditContext editContext,
01328         TextEditorModelModifier modelModifier,
01329         TextEditorViewModelModifier viewModelModifier,
01330         MouseEventArgs mouseEventArgs,
01331         TextEditorComponentData componentData,
01332         ILuthetusTextEditorComponentRenderers textEditorComponentRenderers,
01333         ResourceUri resourceUri)
01334     {
01335         // Lazily calculate row and column index a second time. Otherwise one has to calculate it every mouse moved event.
01336         var rowAndColumnIndex = await EventUtils.CalculateRowAndColumnIndex(
01337                 resourceUri,
01338                 viewModelModifier.ViewModel.ViewModelKey,
01339                 mouseEventArgs,
01340                 componentData,
01341                 editContext)
01342             .ConfigureAwait(false);
01343 
01344         var textEditorDimensions = viewModelModifier.ViewModel.TextEditorDimensions;
01345         var scrollbarDimensions = viewModelModifier.ViewModel.ScrollbarDimensions;
01346     
01347         var relativeCoordinatesOnClick = new RelativeCoordinates(
01348             mouseEventArgs.ClientX - textEditorDimensions.BoundingClientRectLeft,
01349             mouseEventArgs.ClientY - textEditorDimensions.BoundingClientRectTop,
01350             scrollbarDimensions.ScrollLeft,
01351             scrollbarDimensions.ScrollTop);
01352 
01353         var cursorPositionIndex = modelModifier.GetPositionIndex(new TextEditorCursor(
01354             rowAndColumnIndex.rowIndex,
01355             rowAndColumnIndex.columnIndex,
01356             true));
01357 
01358         var foundMatch = false;
01359 
01360         var symbols = modelModifier.CompilerService.GetSymbolsFor(modelModifier.ResourceUri);
01361         var diagnostics = modelModifier.CompilerService.GetDiagnosticsFor(modelModifier.ResourceUri);
01362 
01363         if (diagnostics.Length != 0)
01364         {
01365             foreach (var diagnostic in diagnostics)
01366             {
01367                 if (cursorPositionIndex >= diagnostic.TextSpan.StartingIndexInclusive &&
01368                     cursorPositionIndex < diagnostic.TextSpan.EndingIndexExclusive)
01369                 {
01370                     // Prefer showing a diagnostic over a symbol when both exist at the mouse location.
01371                     foundMatch = true;
01372 
01373                     var parameterMap = new Dictionary<string, object?>
01374                     {
01375                         {
01376                             nameof(ITextEditorDiagnosticRenderer.Diagnostic),
01377                             diagnostic
01378                         }
01379                     };
01380 
01381                     viewModelModifier.ViewModel = viewModelModifier.ViewModel with
01382                     {
01383                         TooltipViewModel = new(
01384                             textEditorComponentRenderers.DiagnosticRendererType,
01385                             parameterMap,
01386                             relativeCoordinatesOnClick,
01387                             null,
01388                             componentData.ContinueRenderingTooltipAsync)
01389                     };
01390                 }
01391             }
01392         }
01393 
01394         if (!foundMatch && symbols.Length != 0)
01395         {
01396             foreach (var symbol in symbols)
01397             {
01398                 if (cursorPositionIndex >= symbol.TextSpan.StartingIndexInclusive &&
01399                     cursorPositionIndex < symbol.TextSpan.EndingIndexExclusive)
01400                 {
01401                     foundMatch = true;
01402 
01403                     var parameters = new Dictionary<string, object?>
01404                     {
01405                         {
01406                             nameof(ITextEditorSymbolRenderer.Symbol),
01407                             symbol
01408                         }
01409                     };
01410 
01411                     viewModelModifier.ViewModel = viewModelModifier.ViewModel with
01412                     {
01413                         TooltipViewModel = new(
01414                             textEditorComponentRenderers.SymbolRendererType,
01415                             parameters,
01416                             relativeCoordinatesOnClick,
01417                             null,
01418                             componentData.ContinueRenderingTooltipAsync)
01419                     };
01420                 }
01421             }
01422         }
01423 
01424         if (!foundMatch)
01425         {
01426             viewModelModifier.ViewModel = viewModelModifier.ViewModel with
01427             {
01428                 TooltipViewModel = null
01429             };
01430         }
01431 
01432         // TODO: Measure the tooltip, and reposition if it would go offscreen.
01433     }
01434     
01441     public static void ShowDropdown(
01442         ITextEditorEditContext editContext,
01443         TextEditorModelModifier modelModifier,
01444         TextEditorViewModelModifier viewModelModifier,
01445         CursorModifierBagTextEditor cursorModifierBag,
01446         TextEditorCursorModifier primaryCursor,
01447         IDispatcher dispatcher,
01448         double? leftOffset,
01449         double? topOffset,
01450         Type componentType,
01451         Dictionary<string, object?>? componentParameters)
01452     {
01453         var dropdownKey = new Key<DropdownRecord>(viewModelModifier.ViewModel.ViewModelKey.Guid);
01454         
01455         if (leftOffset is null)
01456         {
01457             leftOffset = primaryCursor.ColumnIndex * viewModelModifier.ViewModel.CharAndLineMeasurements.CharacterWidth;
01458             
01459             // Tab key column offset
01460             var tabsOnSameRowBeforeCursor = modelModifier.GetTabCountOnSameLineBeforeCursor(
01461                 primaryCursor.LineIndex,
01462                 primaryCursor.ColumnIndex);
01463 
01464             // 1 of the character width is already accounted for
01465             var extraWidthPerTabKey = TextEditorModel.TAB_WIDTH - 1;
01466 
01467             leftOffset += extraWidthPerTabKey *
01468                 tabsOnSameRowBeforeCursor *
01469                 viewModelModifier.ViewModel.CharAndLineMeasurements.CharacterWidth;
01470                 
01471             leftOffset -= viewModelModifier.ViewModel.ScrollbarDimensions.ScrollLeft;
01472         }
01473         
01474         topOffset ??= (primaryCursor.LineIndex + 1) *
01475             viewModelModifier.ViewModel.CharAndLineMeasurements.LineHeight -
01476             viewModelModifier.ViewModel.ScrollbarDimensions.ScrollTop;
01477         
01478         var dropdownRecord = new DropdownRecord(
01479             dropdownKey,
01480             viewModelModifier.ViewModel.TextEditorDimensions.BoundingClientRectLeft + leftOffset.Value,
01481             viewModelModifier.ViewModel.TextEditorDimensions.BoundingClientRectTop + topOffset.Value,
01482             componentType,
01483             componentParameters,
01484             viewModelModifier.ViewModel.FocusAsync)
01485         {
01486             ShouldShowOutOfBoundsClickDisplay = false
01487         };
01488 
01489         dispatcher.Dispatch(new DropdownState.RegisterAction(dropdownRecord));
01490     }
01491     
01492     public static void RemoveDropdown(
01493         ITextEditorEditContext editContext,
01494         TextEditorViewModelModifier viewModelModifier,
01495         IDispatcher dispatcher)
01496     {
01497         viewModelModifier.ViewModel = viewModelModifier.ViewModel with
01498         {
01499             MenuKind = MenuKind.None
01500         };
01501     
01502         var dropdownKey = new Key<DropdownRecord>(viewModelModifier.ViewModel.ViewModelKey.Guid);
01503         dispatcher.Dispatch(new DropdownState.DisposeAction(dropdownKey));
01504     }
01505     
01506     public static void ShowContextMenu(
01507         ITextEditorEditContext editContext,
01508         TextEditorModelModifier modelModifier,
01509         TextEditorViewModelModifier viewModelModifier,
01510         CursorModifierBagTextEditor cursorModifierBag,
01511         TextEditorCursorModifier primaryCursor,
01512         IDispatcher dispatcher,
01513         TextEditorComponentData componentData)
01514     {
01515         viewModelModifier.ViewModel = viewModelModifier.ViewModel with
01516         {
01517             MenuKind = MenuKind.ContextMenu
01518         };
01519     
01520         ShowDropdown(
01521             editContext,
01522             modelModifier,
01523             viewModelModifier,
01524             cursorModifierBag,
01525             primaryCursor,
01526             dispatcher,
01527             leftOffset: null,
01528             topOffset: null,
01529             typeof(Luthetus.TextEditor.RazorLib.TextEditors.Displays.Internals.ContextMenu),
01530             new Dictionary<string, object?>
01531             {
01532                 {
01533                     nameof(Luthetus.TextEditor.RazorLib.TextEditors.Displays.Internals.ContextMenu.TextEditorViewModelDisplay),
01534                     componentData.TextEditorViewModelDisplay
01535                 },
01536             });
01537     }
