00041     public static TextEditorDiffResult Calculate(
00042         ResourceUri inResourceUri,
00043         string inText,
00044         ResourceUri outResourceUri,
00045         string outText)
00046     {
00047         // Need to build a square two dimensional array.
00048         //
00049         // Envisioning that:
00050         //     -Each character of 'inText' represents a row.
00051         //     -Each character of 'outText' represents a column.
00052         
00053         var squaredSize = Math.Max(inText.Length, outText.Length);
00054 
00055         var diffMatrix = new TextEditorDiffCell[squaredSize, squaredSize];
00056 
00057         (int sourceWeight, int inIndex, int outIndex) highestSourceWeightTuple = (-1, -1, -1);
00058 
00059         for (int inIndex = 0; inIndex < inText.Length; inIndex++)
00060         {
00061             char inCharValue = inText[inIndex];
00062 
00063             for (int outIndex = 0; outIndex < outText.Length; outIndex++)
00064             {
00065                 char outCharValue = outText[outIndex];
00066 
00067                 var weight = GetLargestWeightPriorToCurrentPosition(diffMatrix, inIndex, outIndex);
00068                 var isSourceWeight = false;
00069 
00070                 if (inCharValue == outCharValue)
00071                 {
00072                     var cellSourceWeight = weight + 1;
00073 
00074                     if (cellSourceWeight > weight)
00075                     {
00076                         weight = cellSourceWeight;
00077                         isSourceWeight = true;
00078                     }
00079 
00080                     if (cellSourceWeight > highestSourceWeightTuple.sourceWeight)
00081                         highestSourceWeightTuple = (cellSourceWeight, inIndex, outIndex);
00082                 }
00083 
00084                 diffMatrix[inIndex, outIndex] = new TextEditorDiffCell(
00085                     inCharValue, outCharValue, weight, isSourceWeight);
00086             }
00087 
00088             for (int fabricatedOutIndex = outText.Length; fabricatedOutIndex < squaredSize; fabricatedOutIndex++)
00089             {
00090                 // This for loop sets the cells in the fabricated column in order to create a square matrix
00091                 // in cases where (outTextLength < inTextLength)
00092 
00093                 var rowLargestWeightPriorToCurrentColumn = GetLargestWeightPriorToCurrentPosition(
00094                     diffMatrix,
00095                     inIndex,
00096                     fabricatedOutIndex);
00097 
00098                 diffMatrix[inIndex, fabricatedOutIndex] = new TextEditorDiffCell(
00099                     inCharValue,
00100                     null,
00101                     rowLargestWeightPriorToCurrentColumn,
00102                     false);
00103             }
00104         }
00105 
00106         for (int fabricatedInIndex = inText.Length; fabricatedInIndex < squaredSize; fabricatedInIndex++)
00107         {
00108             // This for loop sets the cells in the fabricated row in order to create a square matrix
00109             // in cases where (inTextLength < outTextLength)
00110             //
00111             // TODO: This logic should to be removed. Instead of looking at this algorithm from the perspective of 'before' and 'after' text. It might be best to have the perspective be 'vertical' and 'horizontal' text. Then the 'vertical' text is equal to the longer of the two string inputs. By having the row count defined by the longer of the two strings you only would ever fabricate columns. This would allow for a lot of code to be removed.
00112 
00113             for (int fabricatedOutIndex = 0; fabricatedOutIndex < squaredSize; fabricatedOutIndex++)
00114             {
00115                 var rowLargestWeightPriorToCurrentColumn = GetLargestWeightPriorToCurrentPosition(
00116                     diffMatrix,
00117                     fabricatedInIndex,
00118                     fabricatedOutIndex);
00119 
00120                 diffMatrix[fabricatedInIndex, fabricatedOutIndex] = new TextEditorDiffCell(
00121                     null,
00122                     null,
00123                     rowLargestWeightPriorToCurrentColumn,
00124                     false);
00125             }
00126         }
00127 
00128         // The abbreviation "lcs" is to mean "Longest Common Subsequence"
00129         var lcsBuilder = new StringBuilder();
00130 
00131         var inPositionIndicesLcsHashSet = new HashSet<int>();
00132         var outPositionIndicesLcsHashSet = new HashSet<int>();
00133 
00134         var inPositionIndicesOfDeletionHashSet = new HashSet<int>();
00135         var outPositionIndicesInsertionHashSet = new HashSet<int>();
00136 
00137         var inPositionIndicesOfModificationHashSet = new HashSet<int>();
00138         var outPositionIndicesOfModificationHashSet = new HashSet<int>();
00139 
00140         // Read the LongestCommonSubsequence by backtracking to the highest weights
00141         {
00142             var runningRowIndex = highestSourceWeightTuple.inIndex;
00143             var runningColumnIndex = highestSourceWeightTuple.outIndex;
00144 
00145             var targetSourceWeight = highestSourceWeightTuple.sourceWeight;
00146 
00147             var foundLargestWeight = false;
00148 
00149             var restoreColumnIndex = runningColumnIndex;
00150 
00151             while (runningRowIndex != -1 && runningColumnIndex != -1)
00152             {
00153                 if (!foundLargestWeight)
00154                     restoreColumnIndex = runningColumnIndex;
00155 
00156                 var cell = diffMatrix[runningRowIndex, runningColumnIndex];
00157 
00158                 if (cell.IsSourceOfRowWeight &&
00159                     cell.Weight == targetSourceWeight)
00160                 {
00161                     targetSourceWeight--;
00162 
00163                     if (cell.BeforeCharValue != cell.AfterCharValue)
00164                         throw new LuthetusTextEditorException($"The {nameof(cell.BeforeCharValue)}:'{cell.BeforeCharValue}' was not equal to the {nameof(cell.AfterCharValue)}:'{cell.AfterCharValue}'");
00165 
00166                     lcsBuilder.Append(cell.BeforeCharValue);
00167 
00168                     // Decoration logic for longest common subsequence
00169                     {
00170                         inPositionIndicesLcsHashSet.Add(runningRowIndex);
00171                         outPositionIndicesLcsHashSet.Add(runningColumnIndex);
00172                     }
00173 
00174                     restoreColumnIndex = runningColumnIndex - 1;
00175 
00176                     // Forces the row index decrementation then a column index restoration.
00177                     runningColumnIndex = 0;
00178                 }
00179                 else
00180                 {
00181                     // Decoration logic
00182                     {
00183                         if (outText.Length > inText.Length && !outPositionIndicesLcsHashSet.Contains(runningColumnIndex))
00184                             outPositionIndicesInsertionHashSet.Add(runningColumnIndex); // Insertion
00185                         else if (inText.Length > outText.Length && runningRowIndex >= outText.Length)
00186                             inPositionIndicesOfDeletionHashSet.Add(runningRowIndex); // Deletion
00187                         // TODO: Else if for modification is not working.
00188                         //
00189                         // else if (cell.BeforeCharValue != cell.AfterCharValue)
00190                         // {
00191                         //     // Modification
00192                         //     beforePositionIndicesOfModificationHashSet.Add(runningRowIndex);
00193                         //     afterPositionIndicesOfModificationHashSet.Add(runningColumnIndex);
00194                         // }
00195                     }
00196                 }
00197 
00198                 if (runningColumnIndex == 0)
00199                 {
00200                     runningColumnIndex = restoreColumnIndex;
00201                     runningRowIndex--;
00202                 }
00203                 else
00204                 {
00205                     runningColumnIndex--;
00206                 }
00207             }
00208         }
00209 
00210         var longestCommonSubsequenceValue = new string(lcsBuilder
00211             .ToString()
00212             .Reverse()
00213             .ToArray());
00214 
00215         var inTextSpans = new List<TextEditorTextSpan>();
00216         var outTextSpans = new List<TextEditorTextSpan>();
00217 
00218         // Decoration logic
00219         {
00220             // Longest common subsequence
00221             {
00222                 inTextSpans.AddRange(GetTextSpans(
00223                     inResourceUri,
00224                     inText,
00225                     inPositionIndicesLcsHashSet,
00226                     (byte)TextEditorDiffDecorationKind.LongestCommonSubsequence));
00227 
00228                 outTextSpans.AddRange(GetTextSpans(
00229                     outResourceUri,
00230                     outText,
00231                     outPositionIndicesLcsHashSet,
00232                     (byte)TextEditorDiffDecorationKind.LongestCommonSubsequence));
00233             }
00234 
00235             // Insertion
00236             {
00237                 outTextSpans.AddRange(GetTextSpans(
00238                     outResourceUri,
00239                     outText,
00240                     outPositionIndicesInsertionHashSet,
00241                     (byte)TextEditorDiffDecorationKind.Insertion));
00242             }
00243 
00244             // Deletion
00245             {
00246                 inTextSpans.AddRange(GetTextSpans(
00247                     inResourceUri,
00248                     inText,
00249                     inPositionIndicesOfDeletionHashSet,
00250                     (byte)TextEditorDiffDecorationKind.Deletion));
00251             }
00252 
00253             // Modification
00254             {
00255                 inTextSpans.AddRange(GetTextSpans(
00256                     inResourceUri,
00257                     inText,
00258                     inPositionIndicesOfModificationHashSet,
00259                     (byte)TextEditorDiffDecorationKind.Modification));
00260 
00261                 outTextSpans.AddRange(GetTextSpans(
00262                     outResourceUri,
00263                     outText,
00264                     outPositionIndicesOfModificationHashSet,
00265                     (byte)TextEditorDiffDecorationKind.Modification));
00266             }
00267         }
00268 
00269         var diffResult = new TextEditorDiffResult(
00270             inText,
00271             outText,
00272             diffMatrix,
00273             highestSourceWeightTuple,
00274             longestCommonSubsequenceValue,
00275             inTextSpans.ToImmutableList(),
00276             outTextSpans.ToImmutableList());
00277 
00278         return diffResult;
00279     }
