00281     private static int GetLargestWeightPriorToCurrentPosition(
00282         TextEditorDiffCell[,] diffMatrix,
00283         int inIndex,
00284         int outIndex)
00285     {
00286         var largestWeight = 0;
00287 
00288         for (int rowI = 0; rowI < inIndex; rowI++)
00289         {
00290             for (int columnI = 0; columnI < outIndex; columnI++)
00291             {
00292                 var currentWeight = diffMatrix[rowI, columnI].Weight;
00293 
00294                 if (currentWeight > largestWeight)
00295                     largestWeight = currentWeight;
00296             }
00297         }
00298 
00299         return largestWeight;
00300     }
