00302     private static List<TextEditorTextSpan> GetTextSpans(
00303         ResourceUri resourceUri,
00304         string sourceText,
00305         HashSet<int> positionIndicesHashSet,
00306         byte decorationByte)
00307     {
00308         var matchTextSpans = new List<TextEditorTextSpan>();
00309 
00310         if (!positionIndicesHashSet.Any())
00311             return matchTextSpans;
00312 
00313         var sortedPositionIndicesThatMatch = positionIndicesHashSet
00314             .OrderBy(x => x)
00315             .ToList();
00316 
00317         // The foreach loop coalesces contiguous indices into a single TextEditorTextSpan
00318         // and the logic that does the coalescing will not write out the final index without this.
00319         sortedPositionIndicesThatMatch.Add(int.MaxValue);
00320 
00321         var startingIndexInclusive = sortedPositionIndicesThatMatch.First();
00322         var endingIndexExclusive = startingIndexInclusive + 1;
00323 
00324         var skipFirstIndex = sortedPositionIndicesThatMatch
00325             .Skip(1)
00326             .ToArray();
00327 
00328         foreach (var index in skipFirstIndex)
00329         {
00330             if (index == endingIndexExclusive)
00331             {
00332                 endingIndexExclusive++;
00333             }
00334             else
00335             {
00336                 var textSpan = new TextEditorTextSpan(
00337                     startingIndexInclusive,
00338                     endingIndexExclusive,
00339                     decorationByte,
00340                     resourceUri,
00341                     sourceText);
00342 
00343                 matchTextSpans.Add(textSpan);
00344 
00345                 startingIndexInclusive = index;
00346                 endingIndexExclusive = index + 1;
00347             }
00348         }
00349 
00350         return matchTextSpans;
00351     }
