00038         [EffectMethod(typeof(StartSearchAction))]
00039         public Task HandleStartSearchAction(IDispatcher dispatcher)
00040         {
00041             _throttleSetSearchQuery.Run(async _ => 
00042             {
00043                 dispatcher.Dispatch(new CancelSearchAction());
00044                 dispatcher.Dispatch(new ClearSearchAction());
00045                 
00046                 var textEditorFindAllState = _textEditorFindAllStateWrap.Value;
00047 
00048                 if (string.IsNullOrWhiteSpace(textEditorFindAllState.StartingDirectoryPath) ||
00049                     string.IsNullOrWhiteSpace(textEditorFindAllState.SearchQuery))
00050                 {
00051                     return;
00052                 }
00053                 
00054                 var cancellationToken = textEditorFindAllState._searchCancellationTokenSource.Token;
00055                 var progressBarModel = new ProgressBarModel();
00056 
00057                 ConstructTreeView(textEditorFindAllState);
00058 
00059                 dispatcher.Dispatch(new SetProgressBarModelAction(progressBarModel));
00060                 
00061                 try
00062                 {
00063                     await StartSearchTask(
00064                         progressBarModel,
00065                         _fileSystemProvider,
00066                         textEditorFindAllState,
00067                         dispatcher,
00068                         cancellationToken);
00069                 }
00070                 catch (Exception e)
00071                 {
00072                     Console.WriteLine(e);
00073                 }
00074             });
00075 
00076             return Task.CompletedTask;
00077         }
